        icon: 'people',
        settings: {
            sensitivity: 50,
            threshold: -32,
            minSilenceLength: 0.5,
            paddingStart: 0.12,
            paddingEnd: 0.08,
            enableTakesDetection: true,
            enableJCut: true,
            jCutLeadIn: 0.25,
            jCutLeadOut: 0.15
        }
    },

    // Reaction video - fast pacing, quick cuts
    reaction: {
        name: 'Reaction',
        description: 'Fast-paced, tight cuts for energy',
        icon: 'bolt',
        settings: {
            sensitivity: 70,
            threshold: -28,
            minSilenceLength: 0.3,
            paddingStart: 0.05,
            paddingEnd: 0.03,
            enableTakesDetection: false,
            enableJCut: false,
            jCutLeadIn: 0,
            jCutLeadOut: 0
        }
    },

    // Tutorial/Educational - preserve thinking pauses
    tutorial: {
        name: 'Tutorial',
        description: 'Preserves teaching pace, J-Cuts enabled',
        icon: 'school',
        settings: {
            sensitivity: 30,
            threshold: -38,
            minSilenceLength: 1.0,
            paddingStart: 0.2,
            paddingEnd: 0.15,
            enableTakesDetection: true,
            enableJCut: true,
            jCutLeadIn: 0.35,
            jCutLeadOut: 0.25
        }
