function initFeatureToggles() {
    // J-Cut toggle
    if (ui.enableJCut && ui.jcutSettings) {
        ui.enableJCut.addEventListener('change', () => {
            ui.jcutSettings.classList.toggle('collapsed', !ui.enableJCut.checked);
