#### **3. B-Roll Stock Footage Settings with Async JSX Execution**
```javascript
// DEOBFUSCATED - Demonstrates jsxExecute() and Select2 integration
function workflowsRenderBrollStepSettings() {
  if (!workflowStepDraft || workflowStepDraft['type'] !== 'broll') {
    return;
  }

  const settings = workflowStepDraft['settings'] || {};
  const frequency = typeof settings['brollFrequency'] === 'number' 
    ? settings['brollFrequency'] 
    : 10;

  $('#wf-range-broll-frequency').val(String(frequency));

  // Method selection (v4, etc.)
  const method = settings['method'] || 'v4';
  $('[name="wf-setting-broll-method"][value="' + method + '"]').prop('checked', true);

  // Stock library selection
  const library = settings['stockLibrary'] || 'storyblocks';
  $('[name="wf-setting-broll-library"][value="' + library + '"]').prop('checked', true);

  // Media type filters
  const mediaTypes = Array.isArray(settings['mediaTypes']) 
    ? settings['mediaTypes'] 
    : ['video'];
  mediaTypes.forEach(type => {
    $('[name="wf-setting-broll-media-type"][value="' + type + '"]').prop('checked', true);
  });

  // Match orientation
  $('#wf-checkbox-broll-match-orientation').prop('checked', !!settings['matchOrientation']);

  // Max clip duration
  const maxDuration = typeof settings['maxClipDuration'] === 'number' 
    ? settings['maxClipDuration'] 
    : 10;
  $('#wf-range-broll-max-clip-duration').val(String(maxDuration));

  // Transition type - ASYNC JSX CALL
  const transitionType = settings['transitionType'] || 'None';
  (async () => {
    try {
      // Execute JSX to get available video transitions
      const transitionListJson = await jsxExecute('getVideoTransitionList()');
      let transitions = [];
      try {
        transitions = JSON.parse(transitionListJson) || [];
      } catch (e) {
        transitions = [];
      }

      const transitionSelect = $('#wf-setting-broll-transition-type');
      if (transitionSelect.length) {
        transitionSelect.empty();
        transitionSelect.append($('<option>', {
          value: 'None',
          text: 'None'
        }));

        transitions.sort().forEach(transition => {
          transitionSelect.append($('<option>', {
            value: transition,
            text: transition
          }));
        });

        // Initialize Select2 if not already initialized
        if (!transitionSelect.hasClass('select2-hidden-accessible')) {
          transitionSelect.css('width', '100%');
          transitionSelect.select2({
            theme: 'firecut',
            width: '100%'
          });
        }

        transitionSelect.val(transitionType).trigger('change.select2');
      }
    } catch (error) {
      $('#wf-setting-broll-transition-type').val(transitionType);
    }
  })();

  // Transition duration (in frames)
  const transitionDuration = typeof settings['transitionDuration'] === 'number' 
    ? settings['transitionDuration'] 
    : 30;
  $('#wf-range-transition-duration').val(String(transitionDuration));
  $('#wf-broll-value-transition-duration').text(transitionDuration + ' frames');

  // Scaling mode
  const scalingMode = settings['scalingMode'] || 'fill';
  $('[name="wf-setting-broll-scaling"][value="' + scalingMode + '"]').prop('checked', true);
}
```

