    76â†’    ui.licenseKeyInput = document.getElementById('licenseKeyInput');
    77â†’    ui.saveLoginBtn = document.getElementById('saveLoginBtn');
    78â†’
    79â†’    // Credit badge
    80â†’    ui.creditBadge = document.getElementById('creditBadge');
