   111â†’// ============================================================================
   112â†’// INITIALIZATION
   113â†’// ============================================================================
   114â†’document.addEventListener('DOMContentLoaded', async () => {
   115â†’    console.log('[SPLICE] Initializing CEP panel...');
   116â†’
   117â†’    // Cache DOM elements
   118â†’    cacheUIElements();
   119â†’
   120â†’    // Load settings
   121â†’    loadSettingsToUI();
   122â†’
   123â†’    // Initialize UI handlers
   124â†’    initOptionsToggle();
   125â†’    initSliders();
   126â†’    initFeatureToggles();
   127â†’    initPresetSelector();
   128â†’    initGoButton();
   129â†’    initPreviewHandlers();
   130â†’    initModals();
   131â†’    initCredits();
   132â†’    initHelpButton();
   133â†’
   134â†’    // Initialize offline detection
   135â†’    if (typeof initOfflineDetection === 'function') {
   136â†’        initOfflineDetection();
   137â†’    }
   138â†’
   139â†’    // Initialize multitrack UI
   140â†’    if (typeof initMultitrackUI === 'function') {
   141â†’        initMultitrackUI();
   142â†’    }
   143â†’
   144â†’    // Initialize animated captions UI
   145â†’    if (typeof initAnimatedCaptions === 'function') {
   146â†’        initAnimatedCaptions();
   147â†’    }
   148â†’
   149â†’    // Initialize text editor UI
   150â†’    if (typeof initTextEditor === 'function') {
   151â†’        initTextEditor();
   152â†’    }
   153â†’
   154â†’    // Initialize text editor section toggle
   155â†’    initTextEditorToggle();
   156â†’
   157â†’    // Initialize social reframe UI
   158â†’    if (typeof initSocialReframe === 'function') {
   159â†’        initSocialReframe();
   160â†’    }
   161â†’
   162â†’    // Initialize social reframe section toggle
   163â†’    initSocialReframeToggle();
   164â†’
   165â†’    // Initialize music UI
   166â†’    if (typeof initMusicModule === 'function') {
   167â†’        initMusicModule();
   168â†’    }
   169â†’
   170â†’    // Initialize music section toggle
   171â†’    initMusicToggle();
   172â†’
   173â†’    // Check JSX connection
   174â†’    await checkJSXConnection();
   175â†’
   176â†’    setStatus('Ready');
   177â†’    console.log('[SPLICE] CEP panel initialized');
   178â†’});
   179â†’
   180â†’// ============================================================================
   181â†’// JSX CONNECTION CHECK
   182â†’// ============================================================================
   183â†’async function checkJSXConnection() {
   184â†’    try {
   185â†’        const result = await jsx.call('getVersion');
   186â†’        console.log('[SPLICE] JSX connection OK, version:', result);
   187â†’        return true;
   188â†’    } catch (e) {
   189â†’        console.warn('[SPLICE] JSX connection failed:', e.message);
   190â†’        setStatus('Premiere Pro connection failed', true);
   191â†’        return false;
   192â†’    }
   193â†’}
   194â†’
   195â†’async function checkSequenceOpen() {
   196â†’    try {
   197â†’        const result = await jsx.call('checkSequenceOpen');
   198â†’        return result === true || result === 'true';
   199â†’    } catch {
   200â†’        return false;
   201â†’    }
   202â†’}
   203â†’
   204â†’// ============================================================================
   205â†’// OPTIONS TOGGLE
   206â†’// ============================================================================
   207â†’function initOptionsToggle() {
   208â†’    if (ui.optionsToggle && ui.optionsPanel) {
   209â†’        ui.optionsToggle.addEventListener('click', () => {
   210â†’            const isExpanded = ui.optionsPanel.classList.toggle('collapsed');
   211â†’            ui.optionsToggle.classList.toggle('expanded', !isExpanded);
   212â†’            saveSettings({ expandedOptions: !isExpanded });
   213â†’        });
   214â†’
   215â†’        // Restore state
   216â†’        const settings = getSettings();
   217â†’        if (!settings.expandedOptions) {
   218â†’            ui.optionsPanel.classList.add('collapsed');
   219â†’        } else {
   220â†’            ui.optionsToggle.classList.add('expanded');
   221â†’        }
   222â†’    }
   223â†’}
   224â†’
   225â†’// ============================================================================
   226â†’// TEXT EDITOR TOGGLE
   227â†’// ============================================================================
   228â†’function initTextEditorToggle() {
   229â†’    const toggle = document.getElementById('textEditorToggle');
   230â†’    const panel = document.getElementById('text-editor-panel');
   231â†’
   232â†’    if (toggle && panel) {
   233â†’        toggle.addEventListener('click', () => {
   234â†’            const isCollapsed = panel.classList.toggle('collapsed');
   235â†’            const icon = toggle.querySelector('.toggle-icon');
   236â†’            if (icon) {
   237â†’                icon.textContent = isCollapsed ? '+' : '-';
   238â†’            }
   239â†’        });
   240â†’    }
   241â†’}
   242â†’
   243â†’// ============================================================================
   244â†’// SOCIAL REFRAME TOGGLE
   245â†’// ============================================================================
   246â†’function initSocialReframeToggle() {
   247â†’    const toggle = document.getElementById('socialReframeToggle');
   248â†’    const panel = document.getElementById('social-reframe-panel');
   249â†’
   250â†’    if (toggle && panel) {
   251â†’        toggle.addEventListener('click', () => {
   252â†’            const isCollapsed = panel.classList.toggle('collapsed');
   253â†’            const icon = toggle.querySelector('.toggle-icon');
   254â†’            if (icon) {
   255â†’                icon.textContent = isCollapsed ? '+' : '-';
   256â†’            }
   257â†’        });
   258â†’    }
   259â†’}
   260â†’
   261â†’// ============================================================================
   262â†’// MUSIC TOGGLE
   263â†’// ============================================================================
   264â†’function initMusicToggle() {
   265â†’    const toggle = document.getElementById('musicToggle');
   266â†’    const panel = document.getElementById('music-panel');
   267â†’
   268â†’    if (toggle && panel) {
   269â†’        toggle.addEventListener('click', () => {
   270â†’            const isCollapsed = panel.classList.toggle('collapsed');
   271â†’            const icon = toggle.querySelector('.toggle-icon');
   272â†’            if (icon) {
   273â†’                icon.textContent = isCollapsed ? '+' : '-';
   274â†’            }
   275â†’        });
