   277â†’}
   278â†’
   279â†’// ============================================================================
   280â†’// SLIDERS
   281â†’// ============================================================================
   282â†’function initSliders() {
   283â†’    // Sensitivity slider
   284â†’    if (ui.sensitivitySlider && ui.sensitivityValue) {
   285â†’        ui.sensitivitySlider.addEventListener('input', () => {
   286â†’            ui.sensitivityValue.textContent = ui.sensitivitySlider.value;
   287â†’            saveSettings({ sensitivity: parseInt(ui.sensitivitySlider.value) });
   288â†’        });
   289â†’    }
   290â†’
   291â†’    // J-Cut sliders
   292â†’    if (ui.jcutLeadIn && ui.jcutLeadInValue) {
   293â†’        ui.jcutLeadIn.addEventListener('input', () => {
   294â†’            const val = (ui.jcutLeadIn.value / 100).toFixed(2);
   295â†’            ui.jcutLeadInValue.textContent = val + 's';
   296â†’            saveSettings({ jcutLeadIn: parseFloat(val) });
   297â†’        });
   298â†’    }
   299â†’
   300â†’    if (ui.jcutLeadOut && ui.jcutLeadOutValue) {
   301â†’        ui.jcutLeadOut.addEventListener('input', () => {
   302â†’            const val = (ui.jcutLeadOut.value / 100).toFixed(2);
   303â†’            ui.jcutLeadOutValue.textContent = val + 's';
   304â†’            saveSettings({ jcutLeadOut: parseFloat(val) });
   305â†’        });
   306â†’    }
   307â†’}
   308â†’
   309â†’// ============================================================================
   310â†’// FEATURE TOGGLES
   311â†’// ============================================================================
   312â†’function initFeatureToggles() {
   313â†’    // J-Cut toggle
   314â†’    if (ui.enableJCut && ui.jcutSettings) {
   315â†’        ui.enableJCut.addEventListener('change', () => {
   316â†’            ui.jcutSettings.classList.toggle('collapsed', !ui.enableJCut.checked);
   317â†’            saveSettings({ enableJCut: ui.enableJCut.checked });
   318â†’        });
   319â†’    }
   320â†’
   321â†’    // Zoom toggle
   322â†’    if (ui.enableZoom && ui.zoomSettings) {
   323â†’        ui.enableZoom.addEventListener('change', () => {
   324â†’            ui.zoomSettings.classList.toggle('collapsed', !ui.enableZoom.checked);
   325â†’            saveSettings({ enableZoom: ui.enableZoom.checked });
   326â†’        });
   327â†’    }
   328â†’
   329â†’    // Chapters toggle
   330â†’    if (ui.enableChapters && ui.chapterSettings) {
   331â†’        ui.enableChapters.addEventListener('change', () => {
   332â†’            ui.chapterSettings.classList.toggle('collapsed', !ui.enableChapters.checked);
   333â†’            saveSettings({ enableChapters: ui.enableChapters.checked });
   334â†’        });
   335â†’    }
   336â†’
   337â†’    // Takes detection toggle
   338â†’    if (ui.enableTakesDetection) {
   339â†’        ui.enableTakesDetection.addEventListener('change', () => {
   340â†’            saveSettings({ enableTakesDetection: ui.enableTakesDetection.checked });
   341â†’        });
   342â†’    }
   343â†’}
   344â†’
   345â†’// ============================================================================
   346â†’// PRESET SELECTOR
   347â†’// ============================================================================
   348â†’// PRESETS is defined in config.js
   349â†’
   350â†’function initPresetSelector() {
   351â†’    if (!ui.presetSelector) return;
   352â†’
   353â†’    ui.presetSelector.addEventListener('change', () => {
   354â†’        const presetId = ui.presetSelector.value;
   355â†’        applyPreset(presetId);
   356â†’    });
   357â†’
   358â†’    // Load current preset
   359â†’    const settings = getSettings();
   360â†’    if (settings.activePreset) {
   361â†’        ui.presetSelector.value = settings.activePreset;
   362â†’    }
   363â†’}
   364â†’
   365â†’function applyPreset(presetId) {
   366â†’    const preset = PRESETS[presetId];
   367â†’    if (!preset) return;
   368â†’
   369â†’    // Custom preset - keep current settings
   370â†’    if (presetId === 'custom' || !preset.settings) {
   371â†’        saveSettings({ activePreset: 'custom' });
   372â†’        setStatus('Using custom settings');
   373â†’        return;
   374â†’    }
   375â†’
   376â†’    const settings = preset.settings;
   377â†’
   378â†’    // Apply to UI
   379â†’    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   380â†’        ui.sensitivitySlider.value = settings.sensitivity;
   381â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   382â†’    }
   383â†’
   384â†’    if (ui.enableJCut) {
   385â†’        ui.enableJCut.checked = settings.enableJCut || false;
   386â†’        if (ui.jcutSettings) {
   387â†’            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   388â†’        }
   389â†’    }
   390â†’
   391â†’    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   392â†’        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   393â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   394â†’    }
   395â†’
   396â†’    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   397â†’        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   398â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   399â†’    }
   400â†’
   401â†’    // Save merged settings
   402â†’    saveSettings({
   403â†’        activePreset: presetId,
   404â†’        sensitivity: settings.sensitivity,
   405â†’        threshold: settings.threshold,
   406â†’        minSilenceLength: settings.minSilenceLength,
   407â†’        paddingStart: settings.paddingStart,
   408â†’        paddingEnd: settings.paddingEnd,
   409â†’        enableTakesDetection: settings.enableTakesDetection,
   410â†’        enableJCut: settings.enableJCut || false,
   411â†’        jcutLeadIn: settings.jCutLeadIn || 0.3,
   412â†’        jcutLeadOut: settings.jCutLeadOut || 0.2
   413â†’    });
   414â†’
   415â†’    setStatus(`Applied ${preset.name} preset`);
   416â†’}
   417â†’
   418â†’// ============================================================================
   419â†’// LOAD SETTINGS TO UI
   420â†’// ============================================================================
   421â†’function loadSettingsToUI() {
   422â†’    const settings = getSettings();
   423â†’
   424â†’    if (ui.sensitivitySlider) {
   425â†’        ui.sensitivitySlider.value = settings.sensitivity;
   426â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   427â†’    }
   428â†’
   429â†’    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   430â†’    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   431â†’    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   432â†’    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   433â†’
   434â†’    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   435â†’    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   436â†’    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   437â†’
   438â†’    if (ui.jcutLeadIn) {
   439â†’        ui.jcutLeadIn.value = Math.round(settings.jcutLeadIn * 100);
   440â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jcutLeadIn + 's';
   441â†’    }
   442â†’    if (ui.jcutLeadOut) {
   443â†’        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
   444â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
   445â†’    }
   446â†’
   447â†’    if (ui.presetSelector && settings.activePreset) {
   448â†’        ui.presetSelector.value = settings.activePreset;
   449â†’    }
   450â†’}
   451â†’
   452â†’// ============================================================================
   453â†’// GO BUTTON - MAIN WORKFLOW
   454â†’// ============================================================================
   455â†’function initGoButton() {
   456â†’    if (!ui.goBtn) return;
   457â†’
   458â†’    ui.goBtn.addEventListener('click', async () => {
   459â†’        if (isOperationInProgress) return;
   460â†’        await runDetection();
   461â†’    });
   462â†’}
   463â†’
   464â†’async function runDetection() {
   465â†’    if (isOperationInProgress) return;
   466â†’
   467â†’    // Check sequence
   468â†’    const hasSequence = await checkSequenceOpen();
   469â†’    if (!hasSequence) {
   470â†’        setStatus('Please open a sequence first', true);
   471â†’        return;
   472â†’    }
   473â†’
   474â†’    // Check online
   475â†’    if (!isOnline()) {
   476â†’        setStatus('Offline - Check your connection', true);
   477â†’        return;
   478â†’    }
   479â†’
   480â†’    isOperationInProgress = true;
   481â†’    ui.goBtn.disabled = true;
   482â†’    showProgress('Detecting silences...');
   483â†’
   484â†’    try {
   485â†’        const settings = getSettings();
   486â†’
   487â†’        // Get sequence info
   488â†’        const seqInfo = await jsx.call('getActiveSequence');
   489â†’        console.log('[SPLICE] Sequence info:', seqInfo);
   490â†’
   491â†’        // Store sequence info for later use
   492â†’        currentSequenceInfo = seqInfo;
   493â†’        window.spliceState.activeSequence = seqInfo;
   494â†’        if (seqInfo && seqInfo.videoPath) {
   495â†’            window.spliceState.currentVideoPath = seqInfo.videoPath;
   496â†’        }
   497â†’
   498â†’        // Export audio via JSX for silence detection
   499â†’        updateProgress(10, 'Exporting audio...');
   500â†’
   501â†’        let audioFilePath = null;
   502â†’        try {
   503â†’            // Get sequence info for audio export
   504â†’            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   505â†’            if (exportResult && exportResult.success && exportResult.outputPath) {
   506â†’                audioFilePath = exportResult.outputPath;
   507â†’                console.log('[SPLICE] Audio exported to:', audioFilePath);
   508â†’            } else if (exportResult && exportResult.error) {
   509â†’                throw new Error(exportResult.error);
   510â†’            }
   511â†’        } catch (exportError) {
   512â†’            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   513â†’            // Try to get audio from first clip in timeline
   514â†’            try {
   515â†’                const clipInfo = await jsx.call('getFirstClipAudioPath');
   516â†’                if (clipInfo && clipInfo.path) {
   517â†’                    audioFilePath = clipInfo.path;
   518â†’                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   519â†’                }
   520â†’            } catch (clipError) {
   521â†’                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   522â†’            }
   523â†’        }
   524â†’
   525â†’        if (!audioFilePath) {
   526â†’            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   527â†’        }
   528â†’
   529â†’        // Store audio path globally for other modules
   530â†’        currentAudioPath = audioFilePath;
   531â†’        window.spliceState.audioPath = audioFilePath;
   532â†’
   533â†’        // Call backend for silence detection with audio file path
   534â†’        updateProgress(30, 'Detecting silences...');
   535â†’        const silenceResponse = await fetchWithTimeout(
   536â†’            `${getBackendUrl()}/silences-rms`,
   537â†’            {
   538â†’                method: 'POST',
   539â†’                headers: getAuthHeaders(),
   540â†’                body: JSON.stringify({
   541â†’                    wavPath: audioFilePath,
   542â†’                    sensitivity: settings.sensitivity,
   543â†’                    minSilenceLength: settings.minSilenceLength || 0.5
   544â†’                })
   545â†’            }
