   565â†’                        detectTakes: true
   566â†’                    })
   567â†’                }
   568â†’            );
   569â†’
   570â†’            if (analyzeResponse.ok) {
   571â†’                const analyzeData = await analyzeResponse.json();
   572â†’                previewTakes = analyzeData.takes || [];
   573â†’
   574â†’                // Store transcript for captions/music modules
   575â†’                if (analyzeData.transcript) {
