   582â†’
   583â†’        // Detect chapters if enabled
   584â†’        if (settings.enableChapters) {
   585â†’            updateProgress(80, 'Detecting chapters...');
   586â†’            try {
   587â†’                const chapterResponse = await fetchWithTimeout(
   588â†’                    `${getBackendUrl()}/chapters`,
   589â†’                    {
   590â†’                        method: 'POST',
   591â†’                        headers: getAuthHeaders(),
   592â†’                        body: JSON.stringify({
   593â†’                            wavPath: audioFilePath,
   594â†’                            transcript: currentTranscript,
   595â†’                            maxChapters: settings.maxChapters || 10,
   596â†’                            minChapterLength: settings.minChapterLength || 60
   597â†’                        })
   598â†’                    }
   599â†’                );
   600â†’
   601â†’                if (chapterResponse.ok) {
   602â†’                    const chapterData = await chapterResponse.json();
   603â†’                    currentChapters = chapterData.chapters || [];
   604â†’                }
   605â†’            } catch (e) {
   606â†’                console.warn('[SPLICE] Chapter detection failed:', e);
   607â†’            }
   608â†’        }
   609â†’
   610â†’        // Detect zoom points if enabled
   611â†’        if (settings.enableZoom) {
   612â†’            updateProgress(90, 'Detecting zoom points...');
   613â†’            try {
   614â†’                const zoomResponse = await fetchWithTimeout(
   615â†’                    `${getBackendUrl()}/zoom`,
   616â†’                    {
   617â†’                        method: 'POST',
   618â†’                        headers: getAuthHeaders(),
   619â†’                        body: JSON.stringify({
   620â†’                            wavPath: audioFilePath,
   621â†’                            transcript: currentTranscript,
   622â†’                            frequency: settings.zoomFrequency || 'medium',
   623â†’                            preset: settings.zoomPreset || 'medium',
   624â†’                            placement: settings.zoomPlacement || 'sentence_start'
   625â†’                        })
   626â†’                    }
   627â†’                );
   628â†’
   629â†’                if (zoomResponse.ok) {
   630â†’                    const zoomData = await zoomResponse.json();
   631â†’                    currentZooms = zoomData.zoomPoints || [];
   632â†’                }
   633â†’            } catch (e) {
   634â†’                console.warn('[SPLICE] Zoom detection failed:', e);
   635â†’            }
   636â†’        }
   637â†’
   638â†’        // Store results
   639â†’        lastDetectionResult = {
   640â†’            silences: previewSilences,
   641â†’            takes: previewTakes,
   642â†’            chapters: currentChapters,
   643â†’            zooms: currentZooms
