   649â†’        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   650â†’
   651â†’    } catch (error) {
   652â†’        console.error('[SPLICE] Detection error:', error);
   653â†’        setStatus('Detection failed: ' + error.message, true);
   654â†’        hideProgress();
   655â†’    } finally {
