   905â†’async function buildSequence() {
   906â†’    if (isOperationInProgress) return;
   907â†’
   908â†’    isOperationInProgress = true;
   909â†’    setStatus('Building sequence...');
   910â†’
   911â†’    try {
   912â†’        const settings = getSettings();
   913â†’
   914â†’        // Ensure we have sequence info
   915â†’        if (!currentSequenceInfo) {
   916â†’            currentSequenceInfo = await jsx.call('getActiveSequence');
   917â†’        }
   918â†’
   919â†’        // Create cut list from selected silences
   920â†’        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
   921â†’
   922â†’        // Calculate total duration from sequence info or last silence
   923â†’        let duration = 0;
   924â†’        if (currentSequenceInfo && currentSequenceInfo.duration) {
   925â†’            duration = currentSequenceInfo.duration;
   926â†’        } else if (previewSilences.length > 0) {
   927â†’            const lastSilence = previewSilences[previewSilences.length - 1];
   928â†’            duration = lastSilence.end + 10; // Add buffer
   929â†’        }
   930â†’
   931â†’        // Call backend to generate cut list
   932â†’        const cutListResponse = await fetchWithTimeout(
   933â†’            `${getBackendUrl()}/cut-list`,
   934â†’            {
   935â†’                method: 'POST',
   936â†’                headers: getAuthHeaders(),
   937â†’                body: JSON.stringify({
   938â†’                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
   939â†’                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
   940â†’                    duration: duration,
   941â†’                    silences: selectedSilences,
   942â†’                    takes: previewTakes,
   943â†’                    enableTakes: settings.enableTakesDetection,
   944â†’                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
   945â†’                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
   946â†’                })
   947â†’            }
   948â†’        );
   949â†’
   950â†’        if (!cutListResponse.ok) {
   951â†’            throw new Error(await parseErrorResponse(cutListResponse));
   952â†’        }
   953â†’
   954â†’        const cutList = await cutListResponse.json();
   955â†’
   956â†’        // Build sequence via JSX
   957â†’        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
   958â†’
   959â†’        if (result && result.success) {
   960â†’            setStatus(`Built sequence: ${result.sequenceName}`);
   961â†’            hidePreview();
   962â†’        } else {
   963â†’            throw new Error(result?.error || 'Build failed');
   964â†’        }
   965â†’    } catch (error) {
   966â†’        setStatus('Build failed: ' + error.message, true);
   967â†’    } finally {
   968â†’        isOperationInProgress = false;
   969â†’    }
   970â†’}
