   353â†’    if (status) {
   354â†’        status.textContent = message;
   355â†’        status.style.color = isError ? '#dc3545' : '#888';
   356â†’    }
   357â†’}
   358â†’
   359â†’/**
   360â†’ * Debounce function calls
   361â†’ */
   362â†’function debounce(func, wait) {
   363â†’    let timeout;
   364â†’    return function executedFunction(...args) {
   365â†’        const later = () => {
   366â†’            clearTimeout(timeout);
   367â†’            func(...args);
   368â†’        };
   369â†’        clearTimeout(timeout);
   370â†’        timeout = setTimeout(later, wait);
   371â†’    };
   372â†’}
