   619â†’/**
   620â†’ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
   621â†’ */
   622â†’function isValidLicenseKeyFormat(key) {
   623â†’    if (!key || typeof key !== 'string') return false;
   624â†’    const normalized = key.toUpperCase().trim();
   625â†’    const pattern = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;
   626â†’    return pattern.test(normalized);
   627â†’}
