   629â†’/**
   630â†’ * Initialize login modal handlers
   631â†’ */
   632â†’function initLoginModal() {
   633â†’    const loginModal = document.getElementById('loginModal');
   634â†’    const closeLoginBtn = document.getElementById('closeLoginBtn');
   635â†’    const saveLoginBtn = document.getElementById('saveLoginBtn');
   636â†’    const licenseKeyInput = document.getElementById('licenseKeyInput');
   637â†’    const creditBadge = document.getElementById('creditBadge');
   638â†’
   639â†’    // Handle credit badge click - retry on error, or show login modal
   640â†’    if (creditBadge) {
   641â†’        creditBadge.addEventListener('click', async () => {
   642â†’            if (creditBadge.classList.contains('error')) {
   643â†’                creditBadge.textContent = '...';
   644â†’                if (typeof refreshCredits === 'function') {
   645â†’                    await refreshCredits();
   646â†’                }
   647â†’                return;
   648â†’            }
   649â†’            showLoginModal();
   650â†’        });
   651â†’    }
   652â†’
   653â†’    // Close login modal
   654â†’    if (closeLoginBtn && loginModal) {
   655â†’        closeLoginBtn.addEventListener('click', () => {
   656â†’            loginModal.classList.add('hidden');
   657â†’        });
   658â†’    }
   659â†’
   660â†’    // Close on backdrop click
   661â†’    if (loginModal) {
   662â†’        loginModal.addEventListener('click', (e) => {
   663â†’            if (e.target === loginModal) {
   664â†’                loginModal.classList.add('hidden');
   665â†’            }
   666â†’        });
   667â†’    }
   668â†’
   669â†’    // Activate license key
   670â†’    if (saveLoginBtn) {
   671â†’        saveLoginBtn.addEventListener('click', async () => {
   672â†’            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
   673â†’
   674â†’            if (!licenseKey) {
   675â†’                showLoginError('Please enter your license key');
   676â†’                return;
   677â†’            }
   678â†’
   679â†’            if (!isValidLicenseKeyFormat(licenseKey)) {
   680â†’                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
   681â†’                return;
   682â†’            }
   683â†’
   684â†’            saveLoginBtn.disabled = true;
   685â†’            saveLoginBtn.textContent = 'Activating...';
   686â†’
   687â†’            try {
   688â†’                const response = await fetchWithTimeout(`${getBackendUrl()}/license/activate`, {
   689â†’                    method: 'POST',
   690â†’                    headers: { 'Content-Type': 'application/json' },
   691â†’                    body: JSON.stringify({ key: licenseKey })
   692â†’                }, SPLICE_CONFIG.FETCH_TIMEOUT);
   693â†’
   694â†’                const result = await response.json();
   695â†’
   696â†’                if (!result.success) {
   697â†’                    showLoginError(result.error || 'Activation failed');
   698â†’                    return;
   699â†’                }
   700â†’
   701â†’                saveSettings({ customerId: result.customerId });
   702â†’                loginModal?.classList.add('hidden');
   703â†’
