   850→
   851→/**
   852→ * Initialize offline detection
   853→ */
   854→function initOfflineDetection() {
   855→    // Browser events
   856→    window.addEventListener('online', () => {
   857→        isOnlineState = true;
   858→        updateOfflineUI(true);
   859→    });
   860→
   861→    window.addEventListener('offline', () => {
   862→        isOnlineState = false;
   863→        updateOfflineUI(false);
   864→    });
   865→
   866→    // Periodic check
   867→    offlineCheckInterval = setInterval(async () => {
   868→        try {
   869→            const response = await fetchWithTimeout(
   870→                `${getBackendUrl()}/health`,
   871→                { method: 'GET' },
   872→                5000
   873→            );
   874→            isOnlineState = response.ok;
   875→        } catch {
   876→            isOnlineState = false;
   877→        }
   878→        updateOfflineUI(isOnlineState);
   879→    }, 30000);
   880→}
   881→
   882→/**
   883→ * Update UI based on online state
   884→ */
   885→function updateOfflineUI(online) {
   886→    const goBtn = document.getElementById('goBtn');
   887→    const status = document.getElementById('status');
   888→
   889→    if (goBtn) {
   890→        goBtn.disabled = !online;
