   895→            status.textContent = 'Offline - Check your connection';
   896→            status.style.color = '#dc3545';
   897→        } else {
   898→            // Reset status when back online
   899→            status.textContent = 'Ready';
   900→            status.style.color = '#888';
   901→        }
   902→    }
   903→}
