   906→// UTILITY FUNCTIONS
   907→// ============================================================================
   908→
   909→/**
   910→ * SECURITY: Escape HTML to prevent XSS attacks
   911→ * Use this when inserting untrusted content into innerHTML
   912→ * @param {string} str - String to escape
   913→ * @returns {string} Escaped string safe for innerHTML
   914→ */
   915→function escapeHtml(str) {
   916→    if (str === null || str === undefined) return '';
   917→    if (typeof str !== 'string') str = String(str);
   918→
   919→    const escapeMap = {
   920→        '&': '&amp;',
   921→        '<': '&lt;',
   922→        '>': '&gt;',
   923→        '"': '&quot;',
   924→        "'": '&#x27;',
   925→        '/': '&#x2F;',
   926→        '`': '&#x60;'
   927→    };
   928→
   929→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   930→}
   931→
   932→/**
   933→ * SECURITY: Create safe HTML from template with escaped values
   934→ * @param {TemplateStringsArray} strings - Template strings
   935→ * @param {...any} values - Values to escape
   936→ * @returns {string} HTML with escaped values
   937→ */
   938→function safeHtml(strings, ...values) {
   939→    return strings.reduce((result, str, i) => {
   940→        const value = values[i - 1];
   941→        const escaped = escapeHtml(value);
   942→        return result + escaped + str;
   943→    });
   944→}
   945→
   946→/**
   947→ * Format time in MM:SS or HH:MM:SS
   948→ */
   949→function formatTime(seconds) {
   950→    if (isNaN(seconds) || seconds < 0) return '0:00';
   951→
   952→    const hours = Math.floor(seconds / 3600);
   953→    const minutes = Math.floor((seconds % 3600) / 60);
   954→    const secs = Math.floor(seconds % 60);
   955→
   956→    if (hours > 0) {
   957→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   958→    }
   959→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   960→}
   961→
   962→/**
   963→ * Set status message
   964→ */
   965→function setStatus(message, isError = false) {
   966→    const status = document.getElementById('status');
   967→    if (status) {
   968→        status.textContent = message;
   969→        status.style.color = isError ? '#dc3545' : '#888';
   970→    }
   971→}
   972→
   973→/**
   974→ * Debounce function calls
   975→ */
   976→function debounce(func, wait) {
   977→    let timeout;
   978→    return function executedFunction(...args) {
   979→        const later = () => {
   980→            clearTimeout(timeout);
   981→            func(...args);
   982→        };
   983→        clearTimeout(timeout);
   984→        timeout = setTimeout(later, wait);
   985→    };
   986→}
