  1079→        name: 'Vlog',
  1080→        description: 'Punchy edits for YouTube engagement',
  1081→        icon: 'videocam',
  1082→        settings: {
  1083→            sensitivity: 65,
  1084→            threshold: -30,
  1085→            minSilenceLength: 0.35,
  1086→            paddingStart: 0.08,
  1087→            paddingEnd: 0.05,
  1088→            enableTakesDetection: true,
  1089→            enableJCut: false,
  1090→            jCutLeadIn: 0,
  1091→            jCutLeadOut: 0
  1092→        }
  1093→    }
  1094→};
  1095→
  1096→/**
  1097→ * List of built-in preset IDs
  1098→ */
  1099→const BUILT_IN_PRESET_IDS = ['custom', 'podcast', 'interview', 'reaction', 'tutorial', 'vlog'];
  1100→
  1101→/**
  1102→ * Get all available presets
  1103→ * @returns {Object} All preset definitions
  1104→ */
  1105→function getPresets() {
  1106→    return { ...PRESETS };
  1107→}
  1108→
  1109→/**
  1110→ * Get a specific preset by name
  1111→ * @param {string} presetName - Name of the preset
  1112→ * @returns {Object|null} Preset definition or null if not found
  1113→ */
  1114→function getPreset(presetName) {
  1115→    return PRESETS[presetName] || null;
  1116→}
  1117→
  1118→/**
  1119→ * Check if a preset is a built-in preset
  1120→ * @param {string} id - Preset ID to check
  1121→ * @returns {boolean} True if built-in, false if custom
  1122→ */
  1123→function isBuiltInPreset(id) {
  1124→    return BUILT_IN_PRESET_IDS.includes(id);
  1125→}
  1126→
  1127→/**
  1128→ * Apply a preset to current settings
  1129→ * @param {string} presetName - Name of the preset to apply
  1130→ * @returns {Object} The applied settings
  1131→ */
  1132→function applyPreset(presetName) {
  1133→    const preset = PRESETS[presetName];
  1134→
  1135→    if (!preset) {
  1136→        console.warn(`[SPLICE] Unknown preset: ${presetName}`);
  1137→        return getSettings();
  1138→    }
  1139→
  1140→    // Custom preset uses current settings
  1141→    if (presetName === 'custom' || !preset.settings) {
  1142→        saveSettings({ activePreset: 'custom' });
  1143→        return getSettings();
  1144→    }
  1145→
  1146→    // Apply preset settings
  1147→    const newSettings = {
  1148→        ...preset.settings,
  1149→        activePreset: presetName
  1150→    };
  1151→
  1152→    saveSettings(newSettings);
  1153→    debugLog(`Applied preset: ${preset.name}`);
  1154→
  1155→    return getSettings();
  1156→}
  1157→
  1158→/**
  1159→ * Get the currently active preset
  1160→ * @returns {string} Active preset name
  1161→ */
  1162→function getActivePreset() {
  1163→    const settings = getSettings();
  1164→    return settings.activePreset || 'custom';
  1165→}
  1166→
  1167→/**
  1168→ * Initialize preset selector
  1169→ */
  1170→function initPresetSelector() {
  1171→    const presetSelector = document.getElementById('presetSelector');
  1172→    const sensitivitySlider = document.getElementById('sensitivitySlider');
  1173→
  1174→    if (!presetSelector) return;
  1175→
  1176→    // Set initial value from settings
  1177→    const settings = getSettings();
  1178→    presetSelector.value = settings.activePreset || 'podcast';
  1179→
  1180→    // Handle preset change
  1181→    presetSelector.addEventListener('change', () => {
  1182→        const presetId = presetSelector.value;
  1183→        const appliedSettings = applyPreset(presetId);
  1184→
  1185→        // Update sensitivity slider to match preset
  1186→        if (sensitivitySlider && appliedSettings.sensitivity !== undefined) {
  1187→            sensitivitySlider.value = appliedSettings.sensitivity;
  1188→            // Update display value
  1189→            const sensitivityValue = document.getElementById('sensitivityValue');
  1190→            if (sensitivityValue) {
  1191→                sensitivityValue.textContent = appliedSettings.sensitivity;
  1192→            }
  1193→        }
  1194→
  1195→        // Update takes detection checkbox
  1196→        const enableTakesDetection = document.getElementById('enableTakesDetection');
  1197→        if (enableTakesDetection && appliedSettings.enableTakesDetection !== undefined) {
  1198→            enableTakesDetection.checked = appliedSettings.enableTakesDetection;
  1199→        }
  1200→
  1201→        // Update J-Cut checkbox
  1202→        const enableJCut = document.getElementById('enableJCut');
  1203→        if (enableJCut && appliedSettings.enableJCut !== undefined) {
  1204→            enableJCut.checked = appliedSettings.enableJCut;
  1205→            // Toggle J-Cut settings visibility
  1206→            const jcutSettings = document.getElementById('jcutSettings');
  1207→            if (jcutSettings) {
  1208→                jcutSettings.classList.toggle('collapsed', !appliedSettings.enableJCut);
  1209→            }
  1210→        }
  1211→
  1212→        const preset = getPreset(presetId);
  1213→        if (preset) {
  1214→            setStatus(`Preset: ${preset.name} - ${preset.description}`);
  1215→        }
  1216→    });
  1217→
  1218→    // Switch to custom when user manually changes sensitivity
  1219→    if (sensitivitySlider) {
  1220→        sensitivitySlider.addEventListener('change', () => {
  1221→            if (presetSelector.value !== 'custom') {
  1222→                presetSelector.value = 'custom';
  1223→                saveSettings({ activePreset: 'custom' });
  1224→            }
  1225→        });
  1226→    }
  1227→}
  1228→
  1229→// ============================================================================
  1230→// LOGIN MODAL
  1231→// ============================================================================
  1232→
  1233→/**
  1234→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1235→ * Character set excludes confusing chars: 0/O, 1/I/L
  1236→ */
  1237→function isValidLicenseKeyFormat(key) {
  1238→    if (!key || typeof key !== 'string') return false;
  1239→    const normalized = key.toUpperCase().trim();
  1240→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1241→    return pattern.test(normalized);
  1242→}
  1243→
  1244→/**
  1245→ * Initialize login modal handlers
  1246→ */
  1247→function initLoginModal() {
  1248→    const loginModal = document.getElementById('loginModal');
  1249→    const closeLoginBtn = document.getElementById('closeLoginBtn');
  1250→    const saveLoginBtn = document.getElementById('saveLoginBtn');
  1251→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1252→    const creditBadge = document.getElementById('creditBadge');
  1253→
  1254→    // Handle credit badge click - retry on error, or show login modal
  1255→    if (creditBadge) {
  1256→        creditBadge.addEventListener('click', async () => {
  1257→            if (creditBadge.classList.contains('error')) {
  1258→                creditBadge.textContent = '...';
  1259→                if (typeof refreshCredits === 'function') {
  1260→                    await refreshCredits();
  1261→                }
  1262→                return;
  1263→            }
  1264→            showLoginModal();
  1265→        });
  1266→    }
  1267→
  1268→    // Close login modal
  1269→    if (closeLoginBtn && loginModal) {
  1270→        closeLoginBtn.addEventListener('click', () => {
  1271→            loginModal.classList.add('hidden');
  1272→        });
  1273→    }
  1274→
  1275→    // Close on backdrop click
  1276→    if (loginModal) {
  1277→        loginModal.addEventListener('click', (e) => {
  1278→            if (e.target === loginModal) {
  1279→                loginModal.classList.add('hidden');
  1280→            }
  1281→        });
  1282→    }
  1283→
  1284→    // Activate license key
  1285→    if (saveLoginBtn) {
  1286→        saveLoginBtn.addEventListener('click', async () => {
  1287→            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1288→
  1289→            if (!licenseKey) {
  1290→                showLoginError('Please enter your license key');
  1291→                return;
  1292→            }
  1293→
  1294→            if (!isValidLicenseKeyFormat(licenseKey)) {
  1295→                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1296→                return;
  1297→            }
  1298→
  1299→            saveLoginBtn.disabled = true;
  1300→            saveLoginBtn.textContent = 'Activating...';
  1301→
  1302→            try {
  1303→                // Use /auth/login endpoint which returns JWT tokens
  1304→                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1305→                    method: 'POST',
  1306→                    headers: { 'Content-Type': 'application/json' },
  1307→                    body: JSON.stringify({ licenseKey: licenseKey })
  1308→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1309→
  1310→                const result = await response.json();
  1311→
  1312→                if (!result.success) {
  1313→                    showLoginError(result.error || 'Activation failed');
  1314→                    return;
  1315→                }
  1316→
  1317→                // Store JWT tokens and customer info
  1318→                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1319→                saveSettings({
  1320→                    customerId: result.customerId,
  1321→                    accessToken: result.token,
  1322→                    refreshToken: result.refreshToken,
  1323→                    tokenExpiry: Date.now() + expiresInMs,
  1324→                    licenseKey: licenseKey // Keep for re-authentication
  1325→                });
  1326→
  1327→                loginModal?.classList.add('hidden');
  1328→
  1329→                if (typeof refreshCredits === 'function') {
  1330→                    await refreshCredits();
  1331→                }
  1332→
  1333→                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1334→                setStatus(`License activated! ${tierName} tier`);
  1335→            } catch (err) {
  1336→                console.error('[SPLICE] License activation error:', err);
  1337→
  1338→                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
  1339→                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1340→                debugLog('Error name:', err?.name);
  1341→                debugLog('Error message:', err?.message);
  1342→                debugLog('Error stack:', err?.stack);
  1343→                debugLog('Backend URL:', getBackendUrl());
  1344→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1345→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1346→                debugLog('User agent:', navigator?.userAgent);
  1347→                debugLog('Error constructor:', err?.constructor?.name);
  1348→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1349→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1350→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1351→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1352→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1353→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1354→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1355→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1356→                debugLog('================================================');
  1357→
  1358→                // Provide detailed error message based on error type
  1359→                let errorMessage = 'Connection error. ';
  1360→
  1361→                if (err.message === 'Request timed out') {
  1362→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1363→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
  1364→                    errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
  1365→                } else if (err.message?.includes('CORS')) {
  1366→                    errorMessage += 'CORS error. Contact support.';
  1367→                } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
  1368→                    errorMessage += 'SSL certificate error. Try updating your system certificates.';
  1369→                } else if (err.message) {
  1370→                    errorMessage += err.message;
  1371→                } else {
  1372→                    errorMessage += 'Unknown error. Check console for details.';
  1373→                }
  1374→
  1375→                debugLog('Final error message shown to user:', errorMessage);
  1376→                showLoginError(errorMessage);
  1377→            } finally {
