   780→function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   781→    try {
   782→        var seq = app.project.activeSequence;
   783→        if (!seq) return JSON.stringify({ error: "No active sequence" });
   784→
   785→        // Validate trackType
   786→        if (trackType !== "video" && trackType !== "audio") {
   787→            return JSON.stringify({ error: "Invalid track type: " + trackType });
   788→        }
   789→
   790→        // Parse silences JSON
   791→        var silencesArr;
   792→        try {
   793→            silencesArr = JSON.parse(silences);
   794→        } catch (parseErr) {
   795→            return JSON.stringify({ error: "Invalid silences JSON: " + parseErr.message });
   796→        }
   797→
   798→        // Get tracks and validate index
   799→        var tracks = trackType === "video" ? seq.videoTracks : seq.audioTracks;
   800→        if (trackIndex < 0 || trackIndex >= tracks.numTracks) {
   801→            return JSON.stringify({ error: "Invalid track index: " + trackIndex + " (max: " + (tracks.numTracks - 1) + ")" });
   802→        }
   803→
   804→        var clips = tracks[trackIndex].clips;
   805→        var deleted = 0;
   806→
   807→        // Iterate backwards to avoid index shifting (use numItems, not length)
   808→        for (var i = clips.numItems - 1; i >= 0; i--) {
   809→            var clip = clips[i];
   810→            var start = clip.start.seconds;
   811→            var end = clip.end.seconds;
   812→            var center = (start + end) / 2;
   813→
   814→            for (var j = 0; j < silencesArr.length; j++) {
   815→                var silence = silencesArr[j];
   816→                var silenceCenter = (silence[0] + silence[1]) / 2;
   817→                if (silenceCenter > start && silenceCenter < end) {
   818→                    clip.remove(false, false);
   819→                    deleted++;
   820→                    break;
   821→                }
   822→            }
   823→        }
   824→
   825→        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack: Deleted ' + deleted + ' clips from ' + trackType + ' track ' + trackIndex);
   826→        return JSON.stringify({ deleted: deleted });
   827→    } catch (e) {
   828→        $.writeln('[SPLICE] deleteClipsAtSilencePointsInTrack error: ' + e.message);
   829→        return JSON.stringify({ error: "deleteClipsAtSilencePointsInTrack failed: " + e.message });
   830→    }
   831→}
