   833→function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   834→    silences = JSON.parse(silences);
   835→    deltas = JSON.parse(deltas);
   836→
   837→    var clips;
   838→    if (trackType == "video") {
   839→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   840→    } else if (trackType == "audio") {
   841→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   842→    }
   843→
   844→    for (var i = 0; i < clips.length; i++) {
   845→        var clip = clips[i];
   846→        var end = clip.end.seconds;
   847→        var num_silences = -1;
   848→
   849→        for (var j = 0; j < silences.length; j++) {
   850→            if (end > silences[j][1]) {
   851→                num_silences++;
   852→            }
   853→        }
   854→
   855→        if (num_silences > -1 && num_silences < deltas.length) {
   856→            var delta = deltas[num_silences];
   857→            var t_start = clip.start;
   858→            t_start.seconds = t_start.seconds - delta;
   859→            clip.start = t_start;
   860→            var t_end = clip.end;
   861→            t_end.seconds = t_end.seconds - delta;
   862→            clip.end = t_end;
   863→        }
   864→    }
   865→    return JSON.stringify({ success: true });
   866→}
