  1520→        }
  1521→
  1522→        // Create export preset for WAV
  1523→        var presetPath = encoder.getDefaultPresetPath();
  1524→
  1525→        // Queue the export
  1526→        encoder.encodeSequence(
  1527→            seq,
  1528→            outputPath,
  1529→            presetPath,
  1530→            0, // WorkAreaType: 0 = entire, 1 = work area
  1531→            false // removeOnCompletion
