  1540→/**
  1541→ * Export sequence audio for SPLICE analysis
  1542→ * Uses AME to export WAV audio to temp folder
  1543→ * @returns {Object} { success, outputPath } or { error }
  1544→ */
  1545→function exportSequenceAudioForAnalysis() {
  1546→    try {
  1547→        var seq = app.project.activeSequence;
  1548→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1549→
  1550→        // Generate temp file path
  1551→        var tempFolder = Folder.temp.fsName;
  1552→        var timestamp = new Date().getTime();
  1553→        var outputPath = tempFolder + "/splice_audio_" + timestamp + ".wav";
  1554→
  1555→        // Check if we have audio tracks with content
  1556→        var hasAudio = false;
  1557→        for (var i = 0; i < seq.audioTracks.numTracks; i++) {
  1558→            if (seq.audioTracks[i].clips.numItems > 0) {
  1559→                hasAudio = true;
  1560→                break;
  1561→            }
  1562→        }
  1563→
  1564→        if (!hasAudio) {
  1565→            return JSON.stringify({ error: "Sequence has no audio clips" });
  1566→        }
  1567→
  1568→        // Use AME for export if available
  1569→        if (app.encoder && app.encoder.encodeSequence) {
  1570→            // Find audio-only preset or use default
  1571→            var presetPath = null;
  1572→            try {
  1573→                // Try to find WAV preset in common locations
  1574→                var presetFolders = [
  1575→                    Folder.appData.fsName + "/Adobe/Common/AME/15.0/Presets",
  1576→                    Folder.appData.fsName + "/Adobe/Common/AME/14.0/Presets",
  1577→                    app.path + "/MediaIO/systempresets/58534430_4d756c74-6962697400000000/WAV 48kHz.epr"
  1578→                ];
  1579→
  1580→                for (var i = 0; i < presetFolders.length; i++) {
  1581→                    var presetFile = new File(presetFolders[i]);
