  1666→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1667→    if (!isQEAvailable() && !enableQE()) {
  1668→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1669→    }
  1670→
  1671→    try {
  1672→        var accuracy = 3;
  1673→        var search_start_time = clipStartTime.toFixed(accuracy);
  1674→
  1675→        if (trackType == "audio") {
  1676→            var numClipsInTrack = qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).numItems;
  1677→            for (var j = 0; j < numClipsInTrack; j++) {
  1678→                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1679→                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1680→                    return JSON.stringify({ success: true });
  1681→                }
  1682→            }
  1683→        } else if (trackType == "video") {
  1684→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1685→            for (var j = 0; j < numClipsInTrack; j++) {
  1686→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1687→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1688→                    return JSON.stringify({ success: true });
  1689→                }
  1690→            }
  1691→        }
  1692→        return JSON.stringify({ error: "Clip not found at specified time" });
