        }

        // Create export preset for WAV
        var presetPath = encoder.getDefaultPresetPath();

        // Queue the export
        encoder.encodeSequence(
            seq,
            outputPath,
            presetPath,
            0, // WorkAreaType: 0 = entire, 1 = work area
