@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar: var(--sidebar);
  --color-chart-5: var(--chart-5);
  --color-chart-4: var(--chart-4);
  --color-chart-3: var(--chart-3);
  --color-chart-2: var(--chart-2);
  --color-chart-1: var(--chart-1);
  --color-ring: var(--ring);
  --color-input: var(--input);
  --color-border: var(--border);
  --color-destructive: var(--destructive);
  --color-accent-foreground: var(--accent-foreground);
  --color-accent: var(--accent);
  --color-muted-foreground: var(--muted-foreground);
  --color-muted: var(--muted);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-secondary: var(--secondary);
  --color-primary-foreground: var(--primary-foreground);
  --color-primary: var(--primary);
  --color-popover-foreground: var(--popover-foreground);
  --color-popover: var(--popover);
  --color-card-foreground: var(--card-foreground);
  --color-card: var(--card);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --radius-2xl: calc(var(--radius) + 8px);
  --radius-3xl: calc(var(--radius) + 12px);
  --radius-4xl: calc(var(--radius) + 16px);
}

:root {
  --radius: 0.625rem;
  --background: oklch(0.98 0.005 280);
  --foreground: oklch(0.15 0.02 280);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.15 0.02 280);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.15 0.02 280);
  --primary: oklch(0.55 0.25 280);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.96 0.01 280);
  --secondary-foreground: oklch(0.25 0.05 280);
  --muted: oklch(0.95 0.01 280);
  --muted-foreground: oklch(0.5 0.02 280);
  --accent: oklch(0.92 0.05 280);
  --accent-foreground: oklch(0.25 0.05 280);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.02 280);
  --input: oklch(0.9 0.02 280);
  --ring: oklch(0.55 0.25 280);
  --chart-1: oklch(0.55 0.25 280);
  --chart-2: oklch(0.65 0.2 220);
  --chart-3: oklch(0.6 0.15 180);
  --chart-4: oklch(0.7 0.18 140);
  --chart-5: oklch(0.75 0.2 100);
  --sidebar: oklch(0.98 0.005 280);
  --sidebar-foreground: oklch(0.15 0.02 280);
  --sidebar-primary: oklch(0.55 0.25 280);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.92 0.05 280);
  --sidebar-accent-foreground: oklch(0.25 0.05 280);
  --sidebar-border: oklch(0.9 0.02 280);
  --sidebar-ring: oklch(0.55 0.25 280);
}

.dark {
  --background: oklch(0.12 0.02 280);
  --foreground: oklch(0.95 0.01 280);
  --card: oklch(0.16 0.025 280);
  --card-foreground: oklch(0.95 0.01 280);
  --popover: oklch(0.16 0.025 280);
  --popover-foreground: oklch(0.95 0.01 280);
  --primary: oklch(0.65 0.25 280);
  --primary-foreground: oklch(0.12 0.02 280);
  --secondary: oklch(0.22 0.03 280);
  --secondary-foreground: oklch(0.95 0.01 280);
  --muted: oklch(0.22 0.03 280);
  --muted-foreground: oklch(0.65 0.02 280);
  --accent: oklch(0.25 0.05 280);
  --accent-foreground: oklch(0.95 0.01 280);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(0.25 0.03 280);
  --input: oklch(0.25 0.03 280);
  --ring: oklch(0.65 0.25 280);
  --chart-1: oklch(0.65 0.25 280);
  --chart-2: oklch(0.7 0.2 220);
  --chart-3: oklch(0.65 0.15 180);
  --chart-4: oklch(0.75 0.18 140);
  --chart-5: oklch(0.8 0.2 100);
  --sidebar: oklch(0.16 0.025 280);
  --sidebar-foreground: oklch(0.95 0.01 280);
  --sidebar-primary: oklch(0.65 0.25 280);
  --sidebar-primary-foreground: oklch(0.12 0.02 280);
  --sidebar-accent: oklch(0.25 0.05 280);
  --sidebar-accent-foreground: oklch(0.95 0.01 280);
  --sidebar-border: oklch(0.25 0.03 280);
  --sidebar-ring: oklch(0.65 0.25 280);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* Custom animations */
@keyframes gradient-x {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(139, 92, 246, 0.3);
  }
  50% {
    box-shadow: 0 0 40px rgba(139, 92, 246, 0.6);
  }
}

.animate-gradient-x {
  background-size: 200% 200%;
  animation: gradient-x 15s ease infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

/* Gradient text */
.gradient-text {
  background: linear-gradient(135deg, #8B5CF6 0%, #06B6D4 50%, #8B5CF6 100%);
  background-size: 200% auto;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradient-x 5s ease infinite;
}

/* Hero gradient background */
.hero-gradient {
  background: linear-gradient(135deg,
    rgba(139, 92, 246, 0.1) 0%,
    rgba(6, 182, 212, 0.05) 50%,
    rgba(139, 92, 246, 0.1) 100%
  );
}

.dark .hero-gradient {
  background: linear-gradient(135deg,
    rgba(139, 92, 246, 0.2) 0%,
    rgba(6, 182, 212, 0.1) 50%,
    rgba(139, 92, 246, 0.2) 100%
  );
}

/* Feature card hover effect */
.feature-card {
  transition: all 0.3s ease;
}

.feature-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 40px rgba(139, 92, 246, 0.15);
}

/* Glass morphism */
.glass {
  background: rgba(255, 255, 255, 0.7);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.dark .glass {
  background: rgba(30, 30, 40, 0.7);
}

/* Smooth scroll */
html {
  scroll-behavior: smooth;
}

/* Hide scrollbar but keep functionality */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

