input.content:
"use client";

import { motion } from "framer-motion";
import { Check, X, Minus } from "lucide-react";

const features = [
  { name: "AI Music Generation", splice: true, firecut: false, autopod: false },
  { name: "Song Identification", splice: true, firecut: false, autopod: false },
  { name: "Vocal Isolation", splice: true, firecut: false, autopod: false },
  { name: "Text-Based Editing", splice: true, firecut: false, autopod: false },
  { name: "Animated Captions", splice: "5 templates", firecut: "Basic", autopod: false },
  { name: "Social Reframe", splice: "Face tracking", firecut: false, autopod: "Basic" },
  { name: "Multitrack/Multicam", splice: "Auto-balance", firecut: true, autopod: true },
  { name: "Silence Removal", splice: "RMS + Whisper", firecut: true, autopod: true },
  { name: "Take Detection", splice: "GPT-4o", firecut: true, autopod: false },
  { name: "UXP Support (PP 25.6+)", splice: true, firecut: false, autopod: false },
  { name: "Chapter Detection", splice: "AI + YouTube", firecut: true, autopod: false },
  { name: "Profanity Bleeping", splice: "5 types", firecut: false, autopod: false },
];

function FeatureCell({ value }: { value: boolean | string }) {
  if (value === true) {
    return (
      <div className="flex justify-center">
        <div className="w-6 h-6 rounded-full bg-green-500/20 flex items-center justify-center">
          <Check className="w-4 h-4 text-green-500" />
        </div>
      </div>
    );
  }
  if (value === false) {
    return (
      <div className="flex justify-center">
        <div className="w-6 h-6 rounded-full bg-red-500/20 flex items-center justify-center">
          <X className="w-4 h-4 text-red-500" />
        </div>
      </div>
    );
  }
  return (
    <div className="text-center text-sm font-medium text-green-600 dark:text-green-400">
      {value}
    </div>
  );
}

export function Comparison() {
  return (
    <section className="py-24 bg-muted/30">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Why Editors Switch to{" "}
            <span className="gradient-text">SPLICE</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            The most comprehensive AI editing toolkit. More features, better AI,
            future-proof architecture.
          </p>
        </motion.div>

        {/* Comparison table */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="overflow-x-auto"
        >
          <table className="w-full min-w-[600px] bg-card rounded-xl border border-border/50 overflow-hidden">
            <thead>
              <tr className="border-b border-border/50">
                <th className="text-left p-4 font-medium">Feature</th>
                <th className="p-4 text-center">
                  <div className="font-bold gradient-text text-lg">SPLICE</div>
                  <div className="text-sm text-muted-foreground">$19-149/mo</div>
                </th>
                <th className="p-4 text-center">
                  <div className="font-semibold">FireCut</div>
                  <div className="text-sm text-muted-foreground">$14-29/mo</div>
                </th>
                <th className="p-4 text-center">
                  <div className="font-semibold">AutoPod</div>
                  <div className="text-sm text-muted-foreground">$29/yr</div>
                </th>
              </tr>
            </thead>
            <tbody>
              {features.map((feature, index) => (
                <tr
                  key={feature.name}
                  className={index % 2 === 0 ? "bg-muted/20" : ""}
                >
                  <td className="p-4 font-medium">{feature.name}</td>
                  <td className="p-4">
                    <FeatureCell value={feature.splice} />
                  </td>
                  <td className="p-4">
                    <FeatureCell value={feature.firecut} />
                  </td>
                  <td className="p-4">
                    <FeatureCell value={feature.autopod} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </motion.div>

        {/* Key differentiators */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="mt-12 grid md:grid-cols-2 lg:grid-cols-4 gap-6"
        >
          {[
            {
              title: "Only AI Music Plugin",
              description: "Generate custom background music with scene-aware mood matching",
            },
            {
              title: "30+ API Endpoints",
              description: "Most comprehensive feature set vs competitors' 5-10",
            },
            {
              title: "UXP Architecture",
              description: "Future-proof for Premiere Pro 25.6+ while supporting legacy",
            },
            {
              title: "Text = Video",
              description: "Delete text to delete video - unique editing workflow",
            },
          ].map((item) => (
            <div
              key={item.title}
              className="bg-card/50 rounded-lg p-6 border border-border/50"
            >
              <h4 className="font-semibold mb-2">{item.title}</h4>
              <p className="text-sm text-muted-foreground">{item.description}</p>
            </div>
          ))}
        </motion.div>
      </div>
    </section>
  );
}

