import { NextRequest, NextResponse } from "next/server";
import Stripe from "stripe";

const stripe = new Stripe(process.env.STRIPE_SECRET_KEY!);

const PRICE_IDS: Record<string, string> = {
  starter: process.env.STRIPE_PRICE_STARTER || "price_1SgH4m2L5BseYEbCih347qfr",
  pro: process.env.STRIPE_PRICE_PRO || "price_1SgH4o2L5BseYEbCIoR19lhQ",
  team: process.env.STRIPE_PRICE_TEAM || "price_1SgH4q2L5BseYEbCYTlimb5M",
};

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { plan, email, successUrl, cancelUrl } = body;

    if (!plan || !PRICE_IDS[plan]) {
      return NextResponse.json(
        { error: "Invalid plan selected" },
        { status: 400 }
      );
    }

    const priceId = PRICE_IDS[plan];

    // Create Stripe checkout session
    const session = await stripe.checkout.sessions.create({
      mode: "subscription",
      payment_method_types: ["card"],
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      customer_email: email || undefined,
      success_url:
        successUrl ||
        `${process.env.NEXT_PUBLIC_BASE_URL || "http://localhost:3000"}/checkout/success?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url:
        cancelUrl ||
        `${process.env.NEXT_PUBLIC_BASE_URL || "http://localhost:3000"}/pricing`,
      subscription_data: {
        trial_period_days: 14,
        metadata: {
          plan,
        },
      },
      metadata: {
        plan,
      },
      allow_promotion_codes: true,
      billing_address_collection: "required",
    });

    return NextResponse.json({
      sessionId: session.id,
      url: session.url,
    });
  } catch (error) {
    console.error("Checkout error:", error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : "Failed to create checkout session",
      },
      { status: 500 }
    );
  }
}

export async function GET(request: NextRequest) {
  const searchParams = request.nextUrl.searchParams;
  const sessionId = searchParams.get("session_id");

  if (!sessionId) {
    return NextResponse.json(
      { error: "Session ID is required" },
      { status: 400 }
    );
  }

  try {
    const session = await stripe.checkout.sessions.retrieve(sessionId, {
      expand: ["subscription", "customer"],
    });

    return NextResponse.json({
      status: session.status,
      customerEmail: session.customer_details?.email,
      plan: session.metadata?.plan,
      subscriptionId:
        typeof session.subscription === "string"
          ? session.subscription
          : session.subscription?.id,
    });
  } catch (error) {
    console.error("Session retrieval error:", error);
    return NextResponse.json(
      {
        error:
          error instanceof Error
            ? error.message
            : "Failed to retrieve session",
      },
      { status: 500 }
    );
  }
}

