"use client";

import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import {
  VolumeX,
  Layers,
  Music,
  MessageSquare,
  Scissors,
  Smartphone,
} from "lucide-react";

const features = [
  {
    icon: VolumeX,
    title: "Silence Removal",
    description:
      "Automatically detect and remove silences, awkward pauses, and dead air with RMS audio analysis.",
    color: "from-violet-500 to-violet-600",
    stats: "10x faster than manual",
  },
  {
    icon: Layers,
    title: "Take Detection",
    description:
      "AI-powered detection identifies the best takes, labels them, and color-codes for easy selection.",
    color: "from-cyan-500 to-cyan-600",
    stats: "GPT-4o powered",
  },
  {
    icon: Music,
    title: "AI Music Generation",
    description:
      "Generate custom background music that matches your video's mood, energy, and scene transitions.",
    color: "from-violet-500 to-purple-600",
    stats: "Mureka AI integration",
  },
  {
    icon: MessageSquare,
    title: "Animated Captions",
    description:
      "Create viral-style captions with MrBeast, Hormozi, and Gaming templates. Keyword highlighting included.",
    color: "from-pink-500 to-rose-600",
    stats: "5 templates included",
  },
  {
    icon: Scissors,
    title: "Text-Based Editing",
    description:
      "Delete text to delete video. Edit your transcript and watch the cuts appear automatically.",
    color: "from-orange-500 to-amber-600",
    stats: "LCS diff algorithm",
  },
  {
    icon: Smartphone,
    title: "Social Reframe",
    description:
      "Convert 16:9 to 9:16, 1:1, and 4:5 with smart face tracking and platform-specific safe zones.",
    color: "from-green-500 to-emerald-600",
    stats: "TikTok, Reels, Shorts",
  },
];

const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 },
};

export function Features() {
  return (
    <section id="features" className="py-24 relative overflow-hidden">
      {/* Background decoration */}
      <div className="absolute inset-0 hero-gradient opacity-50" />

      <div className="container mx-auto px-4 relative z-10">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Everything You Need to{" "}
            <span className="gradient-text">Edit Faster</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            30+ AI-powered features that automate the boring parts of video
            editing so you can focus on creativity.
          </p>
        </motion.div>

        {/* Features grid */}
        <motion.div
          variants={container}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true }}
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-6"
        >
          {features.map((feature) => (
            <motion.div key={feature.title} variants={item}>
              <Card className="feature-card h-full bg-card/50 backdrop-blur border-border/50 hover:border-primary/50">
                <CardContent className="p-6">
                  {/* Icon */}
                  <div
                    className={`w-12 h-12 rounded-lg bg-gradient-to-br ${feature.color} flex items-center justify-center mb-4`}
                  >
                    <feature.icon className="w-6 h-6 text-white" />
                  </div>

                  {/* Content */}
                  <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                  <p className="text-muted-foreground mb-4">
                    {feature.description}
                  </p>

                  {/* Stats badge */}
                  <div className="inline-flex items-center px-3 py-1 rounded-full bg-primary/10 text-primary text-sm font-medium">
                    {feature.stats}
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </motion.div>

        {/* Before/After comparison */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="mt-20"
        >
          <div className="bg-card/50 backdrop-blur rounded-2xl border border-border/50 p-8 md:p-12">
            <div className="grid md:grid-cols-2 gap-8 items-center">
              {/* Before */}
              <div className="text-center md:text-left">
                <div className="inline-block px-3 py-1 rounded-full bg-red-500/10 text-red-500 text-sm font-medium mb-4">
                  Without SPLICE
                </div>
                <h3 className="text-2xl md:text-3xl font-bold mb-4">
                  8+ hours editing
                </h3>
                <ul className="space-y-3 text-muted-foreground">
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-red-500" />
                    Manual silence removal
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-red-500" />
                    Scrubbing for best takes
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-red-500" />
                    Licensing stock music
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-red-500" />
                    Typing captions manually
                  </li>
                </ul>
              </div>

              {/* After */}
              <div className="text-center md:text-left">
                <div className="inline-block px-3 py-1 rounded-full bg-green-500/10 text-green-500 text-sm font-medium mb-4">
                  With SPLICE
                </div>
                <h3 className="text-2xl md:text-3xl font-bold mb-4">
                  45 minutes editing
                </h3>
                <ul className="space-y-3 text-muted-foreground">
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-green-500" />
                    One-click silence removal
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-green-500" />
                    AI labels best takes
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-green-500" />
                    Generate custom music
                  </li>
                  <li className="flex items-center gap-2">
                    <span className="w-1.5 h-1.5 rounded-full bg-green-500" />
                    Auto-animated captions
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

