"use client";

import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { motion } from "framer-motion";
import { Check, Sparkles } from "lucide-react";
import Link from "next/link";

const plans = [
  {
    name: "Starter",
    price: "$19",
    period: "/month",
    description: "Perfect for individual creators getting started",
    features: [
      "12 hours of processing/month",
      "Silence removal (RMS + Whisper)",
      "Take detection with GPT-4o",
      "5 AI music generations/month",
      "Basic caption templates",
      "Email support",
    ],
    cta: "Start Free Trial",
    popular: false,
    priceId: "price_1SgH4m2L5BseYEbCih347qfr",
  },
  {
    name: "Pro",
    price: "$49",
    period: "/month",
    description: "For serious creators who need more power",
    features: [
      "30 hours of processing/month",
      "Everything in Starter, plus:",
      "45 min vocal isolation included",
      "20 AI music generations/month",
      "All 5 caption templates",
      "Social reframe with face tracking",
      "Text-based editing",
      "Priority support",
    ],
    cta: "Start Free Trial",
    popular: true,
    priceId: "price_1SgH4o2L5BseYEbCIoR19lhQ",
  },
  {
    name: "Team",
    price: "$149",
    period: "/month",
    description: "For teams and high-volume creators",
    features: [
      "100 hours of processing/month",
      "Everything in Pro, plus:",
      "3 hours vocal isolation included",
      "100 AI music generations/month",
      "Multitrack auto-balance",
      "Chapter detection with YouTube export",
      "Profanity bleeping (5 types)",
      "Dedicated account manager",
    ],
    cta: "Contact Sales",
    popular: false,
    priceId: "price_1SgH4q2L5BseYEbCYTlimb5M",
  },
];

export function Pricing() {
  return (
    <section id="pricing" className="py-24 bg-muted/30">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <Badge
            variant="outline"
            className="mb-4 px-4 py-1.5 text-sm font-medium border-violet-500/50 bg-violet-500/10"
          >
            <Sparkles className="w-3.5 h-3.5 mr-1.5" />
            14-day free trial on all plans
          </Badge>
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Simple, Transparent{" "}
            <span className="gradient-text">Pricing</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            No hidden fees. No credit card required for trial. Cancel anytime.
          </p>
        </motion.div>

        {/* Pricing cards */}
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className={plan.popular ? "md:-mt-4 md:mb-4" : ""}
            >
              <Card
                className={`h-full relative ${
                  plan.popular
                    ? "border-primary shadow-lg shadow-primary/20"
                    : "border-border/50"
                }`}
              >
                {plan.popular && (
                  <div className="absolute -top-3 left-1/2 -translate-x-1/2">
                    <Badge className="bg-gradient-to-r from-violet-600 to-cyan-600">
                      Most Popular
                    </Badge>
                  </div>
                )}

                <CardHeader className="text-center pb-2 pt-8">
                  <h3 className="text-xl font-bold">{plan.name}</h3>
                  <div className="mt-4">
                    <span className="text-4xl font-bold">{plan.price}</span>
                    <span className="text-muted-foreground">{plan.period}</span>
                  </div>
                  <p className="text-sm text-muted-foreground mt-2">
                    {plan.description}
                  </p>
                </CardHeader>

                <CardContent className="pt-4">
                  <ul className="space-y-3 mb-8">
                    {plan.features.map((feature) => (
                      <li key={feature} className="flex items-start gap-2">
                        <Check className="w-5 h-5 text-green-500 shrink-0 mt-0.5" />
                        <span className="text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  <Button
                    className={`w-full ${
                      plan.popular
                        ? "bg-gradient-to-r from-violet-600 to-cyan-600 hover:from-violet-700 hover:to-cyan-700"
                        : ""
                    }`}
                    variant={plan.popular ? "default" : "outline"}
                    size="lg"
                    asChild
                  >
                    <Link href={`/checkout?plan=${plan.name.toLowerCase()}`}>
                      {plan.cta}
                    </Link>
                  </Button>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>

        {/* Overage pricing note */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="mt-12 text-center"
        >
          <p className="text-sm text-muted-foreground">
            Need more? Overage hours are billed at $1/hour.{" "}
            <Link href="/pricing" className="text-primary hover:underline">
              View detailed pricing
            </Link>
          </p>
        </motion.div>
      </div>
    </section>
  );
}

