file.content:
"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { motion } from "framer-motion";
import {
  BookOpen,
  Zap,
  Settings,
  HelpCircle,
  Code,
  Layers,
  ArrowRight,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

const docs = [
  {
    title: "Quick Start",
    description: "Get up and running with SPLICE in under 5 minutes.",
    icon: Zap,
    href: "/docs/quickstart",
    color: "from-green-500 to-emerald-600",
  },
  {
    title: "Installation",
    description: "Detailed installation instructions for CEP and UXP versions.",
    icon: Settings,
    href: "/docs/installation",
    color: "from-blue-500 to-cyan-600",
  },
  {
    title: "Features",
    description: "Explore all features: silence removal, AI music, captions, and more.",
    icon: Layers,
    href: "/docs/features",
    color: "from-violet-500 to-purple-600",
  },
  {
    title: "API Reference",
    description: "Complete API documentation for advanced integrations.",
    icon: Code,
    href: "/docs/api",
    color: "from-orange-500 to-amber-600",
  },
  {
    title: "Tutorials",
    description: "Step-by-step guides for common editing workflows.",
    icon: BookOpen,
    href: "/docs/tutorials",
    color: "from-pink-500 to-rose-600",
  },
  {
    title: "FAQ & Troubleshooting",
    description: "Common questions and solutions to known issues.",
    icon: HelpCircle,
    href: "/docs/faq",
    color: "from-indigo-500 to-violet-600",
  },
];

export default function DocsPage() {
  return (
    <div className="min-h-screen">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4">
          {/* Hero */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-center mb-12"
          >
            <BookOpen className="w-12 h-12 mx-auto mb-4 text-primary" />
            <h1 className="text-4xl md:text-5xl font-bold mb-4">
              <span className="gradient-text">Documentation</span>
            </h1>
            <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
              Everything you need to master SPLICE and supercharge your editing workflow.
            </p>
          </motion.div>

          {/* Docs grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-5xl mx-auto">
            {docs.map((doc, index) => (
              <motion.div
                key={doc.title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <Link href={doc.href}>
                  <Card className="h-full hover:border-primary/50 transition-colors group cursor-pointer">
                    <CardHeader>
                      <div
                        className={`w-12 h-12 rounded-lg bg-gradient-to-br ${doc.color} flex items-center justify-center mb-4`}
                      >
                        <doc.icon className="w-6 h-6 text-white" />
                      </div>
                      <CardTitle className="flex items-center justify-between">
                        {doc.title}
                        <ArrowRight className="w-4 h-4 opacity-0 group-hover:opacity-100 transition-opacity" />
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <p className="text-muted-foreground">{doc.description}</p>
                    </CardContent>
                  </Card>
                </Link>
              </motion.div>
            ))}
          </div>

          {/* Quick links */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.6 }}
            className="mt-16 text-center"
          >
            <h2 className="text-xl font-bold mb-4">Popular Topics</h2>
            <div className="flex flex-wrap justify-center gap-2">
              {[
                "Silence Removal",
                "AI Music Generation",
                "Animated Captions",
                "Text-Based Editing",
                "Social Reframe",
                "Multitrack Editing",
                "License Activation",
                "Troubleshooting",
              ].map((topic) => (
                <Link
                  key={topic}
                  href={`/docs/search?q=${encodeURIComponent(topic)}`}
                  className="px-4 py-2 rounded-full bg-muted hover:bg-muted/80 text-sm transition-colors"
                >
                  {topic}
                </Link>
              ))}
            </div>
          </motion.div>

          {/* Support callout */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.7 }}
            className="mt-16 max-w-2xl mx-auto"
          >
            <Card className="bg-gradient-to-br from-violet-500/10 to-cyan-500/10 border-primary/20">
              <CardContent className="p-6 text-center">
                <h3 className="text-lg font-bold mb-2">
                  Can&apos;t find what you&apos;re looking for?
                </h3>
                <p className="text-muted-foreground mb-4">
                  Our support team is here to help. Reach out anytime.
                </p>
                <a
                  href="mailto:support@splice.app"
                  className="text-primary hover:underline font-medium"
                >
                  support@splice.app
                </a>
              </CardContent>
            </Card>
          </motion.div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

