"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { motion } from "framer-motion";
import {
  Download,
  Monitor,
  CheckCircle,
  AlertCircle,
  Copy,
  ArrowRight,
  Apple,
  MonitorCog,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { useState } from "react";

interface InstallStep {
  step: number;
  title: string;
  description: string;
  code?: string;
}

const downloads = [
  {
    name: "SPLICE for UXP",
    version: "v5.6.0",
    type: "Modern",
    description: "For Premiere Pro 25.6 and newer. Uses Adobe's modern UXP architecture.",
    compatibility: "Premiere Pro 25.6+",
    badge: "Recommended",
    badgeColor: "bg-green-500",
    features: [
      "Faster performance with native APIs",
      "Better stability and memory management",
      "Future-proof architecture",
      "Full feature parity with CEP",
    ],
    downloadUrl: "/downloads/splice-uxp-v5.6.0.zip",
  },
  {
    name: "SPLICE for CEP",
    version: "v5.6.0",
    type: "Legacy",
    description: "For Premiere Pro 14.0 to 25.9. Uses Adobe's legacy CEP architecture.",
    compatibility: "Premiere Pro 14.0 - 25.9",
    badge: "Wide Compatibility",
    badgeColor: "bg-blue-500",
    features: [
      "Works with older Premiere Pro versions",
      "Proven stability (millions of installs)",
      "Full feature set available",
      "Best for mixed version teams",
    ],
    downloadUrl: "/downloads/splice-cep-v5.6.0.zip",
  },
];

const installSteps: Record<string, Record<string, InstallStep[]>> = {
  uxp: {
    mac: [
      {
        step: 1,
        title: "Download and extract",
        description: "Download the ZIP file and extract it to a location you'll remember.",
      },
      {
        step: 2,
        title: "Open UXP Developer Tool",
        description:
          "In Premiere Pro, go to Window > Extensions > UXP Developer Tool. If you don't have it, install from Adobe Exchange.",
      },
      {
        step: 3,
        title: "Load the plugin",
        description:
          'Click "Add" and select the extracted folder. The plugin will appear in your Extensions menu.',
      },
      {
        step: 4,
        title: "Activate your license",
        description:
          "Open SPLICE from Window > Extensions > SPLICE and enter your license key.",
      },
    ],
    windows: [
      {
        step: 1,
        title: "Download and extract",
        description: "Download the ZIP file and extract it to a folder (e.g., C:\\SPLICE).",
      },
      {
        step: 2,
        title: "Open UXP Developer Tool",
        description:
          "In Premiere Pro, go to Window > Extensions > UXP Developer Tool.",
      },
      {
        step: 3,
        title: "Load the plugin",
        description:
          'Click "Add" and browse to the extracted folder.',
      },
      {
        step: 4,
        title: "Activate your license",
        description:
          "Open SPLICE from Window > Extensions > SPLICE and enter your license key.",
      },
    ],
  },
  cep: {
    mac: [
      {
        step: 1,
        title: "Download and extract",
        description: "Download the ZIP file and extract it.",
      },
      {
        step: 2,
        title: "Copy to extensions folder",
        description:
          "Copy the extracted folder to: ~/Library/Application Support/Adobe/CEP/extensions/",
        code: "cp -r splice-cep ~/Library/Application\\ Support/Adobe/CEP/extensions/",
      },
      {
        step: 3,
        title: "Enable unsigned extensions",
        description: "Run this command in Terminal to allow unsigned extensions:",
        code: "defaults write com.adobe.CSXS.11 PlayerDebugMode 1",
      },
      {
        step: 4,
        title: "Restart Premiere Pro",
        description:
          "Fully quit and reopen Premiere Pro. SPLICE will appear in Window > Extensions.",
      },
    ],
    windows: [
      {
        step: 1,
        title: "Download and extract",
        description: "Download the ZIP file and extract it.",
      },
      {
        step: 2,
        title: "Copy to extensions folder",
        description:
          "Copy the extracted folder to: C:\\Program Files (x86)\\Common Files\\Adobe\\CEP\\extensions\\",
      },
      {
        step: 3,
        title: "Enable unsigned extensions",
        description:
          "Open Registry Editor and navigate to HKEY_CURRENT_USER\\Software\\Adobe\\CSXS.11. Create a string value named PlayerDebugMode with value 1.",
      },
      {
        step: 4,
        title: "Restart Premiere Pro",
        description:
          "Fully quit and reopen Premiere Pro. SPLICE will appear in Window > Extensions.",
      },
    ],
  },
};

export default function DownloadPage() {
  const [selectedVersion, setSelectedVersion] = useState<"uxp" | "cep">("uxp");
  const [selectedOS, setSelectedOS] = useState<"mac" | "windows">("mac");
  const [copiedStep, setCopiedStep] = useState<number | null>(null);

  const copyToClipboard = (text: string, step: number) => {
    navigator.clipboard.writeText(text);
    setCopiedStep(step);
    setTimeout(() => setCopiedStep(null), 2000);
  };

  const currentSteps = installSteps[selectedVersion][selectedOS];

  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24">
        {/* Hero section */}
        <section className="py-16 bg-[#0a0f1a]">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <div className="w-16 h-16 rounded-xl bg-[#00D4FF]/10 flex items-center justify-center mx-auto mb-4">
                <Download className="w-8 h-8 text-[#00D4FF]" />
              </div>
              <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
                Download <span className="gradient-text">SPLICE</span>
              </h1>
              <p className="text-lg text-gray-400 max-w-2xl mx-auto">
                Choose the version that matches your Premiere Pro installation.
                Both versions have the same features.
              </p>
            </motion.div>
          </div>
        </section>

        {/* Download options */}
        <section className="py-16">
          <div className="container mx-auto px-4">
            <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
              {downloads.map((download, index) => (
                <motion.div
                  key={download.name}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <Card
                    className={`h-full bg-[#0d1420] ${
                      download.type === "Modern"
                        ? "border-[#00D4FF] shadow-lg shadow-[#00D4FF]/10"
                        : "border-gray-800"
                    }`}
                  >
                    <CardHeader>
                      <div className="flex items-start justify-between">
                        <div>
                          <Badge className={download.type === "Modern" ? "bg-[#00D4FF] text-black" : "bg-blue-500"}>
                            {download.badge}
                          </Badge>
                          <CardTitle className="mt-2 text-white">{download.name}</CardTitle>
                        </div>
                        <div className="text-right">
                          <div className="font-mono text-sm text-white">{download.version}</div>
                          <div className="text-xs text-gray-400">
                            {download.type}
                          </div>
                        </div>
                      </div>
                      <p className="text-sm text-gray-400 mt-2">
                        {download.description}
                      </p>
                    </CardHeader>
                    <CardContent>
                      <div className="mb-4">
                        <div className="flex items-center gap-2 text-sm font-medium mb-2 text-white">
                          <Monitor className="w-4 h-4 text-[#00D4FF]" />
                          {download.compatibility}
                        </div>
                      </div>

                      <ul className="space-y-2 mb-6">
                        {download.features.map((feature) => (
                          <li key={feature} className="flex items-start gap-2 text-sm text-gray-300">
                            <CheckCircle className="w-4 h-4 text-[#00D4FF] shrink-0 mt-0.5" />
                            {feature}
                          </li>
                        ))}
                      </ul>

                      <Button
                        className={`w-full ${
                          download.type === "Modern"
                            ? "bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                            : "border-gray-700 text-white hover:bg-white/10"
                        }`}
                        variant={download.type === "Modern" ? "default" : "outline"}
                        onClick={() =>
                          setSelectedVersion(
                            download.type === "Modern" ? "uxp" : "cep"
                          )
                        }
                        asChild
                      >
                        <a href={download.downloadUrl}>
                          <Download className="w-4 h-4 mr-2" />
                          Download {download.version}
                        </a>
                      </Button>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}
            </div>
          </div>
        </section>

        {/* Installation guide */}
        <section className="py-16 bg-[#0a0f1a]">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              className="max-w-3xl mx-auto"
            >
              <div className="text-center mb-8">
                <h2 className="text-2xl md:text-3xl font-bold mb-2 text-white">
                  Installation Guide
                </h2>
                <p className="text-gray-400">
                  Follow these steps to install SPLICE
                </p>
              </div>

              {/* Version/OS selector */}
              <div className="flex flex-col sm:flex-row gap-4 justify-center mb-8">
                <div className="flex items-center gap-2 justify-center">
                  <span className="text-sm text-gray-400">Version:</span>
                  <div className="inline-flex rounded-lg border border-gray-700 p-1 bg-[#0d1420]">
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                        selectedVersion === "uxp"
                          ? "bg-[#00D4FF] text-black"
                          : "text-gray-300 hover:bg-white/10"
                      }`}
                      onClick={() => setSelectedVersion("uxp")}
                    >
                      UXP
                    </button>
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                        selectedVersion === "cep"
                          ? "bg-[#00D4FF] text-black"
                          : "text-gray-300 hover:bg-white/10"
                      }`}
                      onClick={() => setSelectedVersion("cep")}
                    >
                      CEP
                    </button>
                  </div>
                </div>

                <div className="flex items-center gap-2 justify-center">
                  <span className="text-sm text-gray-400">OS:</span>
                  <div className="inline-flex rounded-lg border border-gray-700 p-1 bg-[#0d1420]">
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${
                        selectedOS === "mac"
                          ? "bg-[#00D4FF] text-black"
                          : "text-gray-300 hover:bg-white/10"
                      }`}
                      onClick={() => setSelectedOS("mac")}
                    >
                      <Apple className="w-4 h-4" />
                      macOS
                    </button>
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${
                        selectedOS === "windows"
                          ? "bg-[#00D4FF] text-black"
                          : "text-gray-300 hover:bg-white/10"
                      }`}
                      onClick={() => setSelectedOS("windows")}
                    >
                      <MonitorCog className="w-4 h-4" />
                      Windows
                    </button>
                  </div>
                </div>
              </div>

              {/* Steps */}
              <Card>
                <CardContent className="p-6">
                  <div className="space-y-6">
                    {currentSteps.map((step, index) => (
                      <div key={step.step}>
                        <div className="flex items-start gap-4">
                          <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center text-primary font-bold shrink-0">
                            {step.step}
                          </div>
                          <div className="flex-1">
                            <h3 className="font-semibold mb-1">{step.title}</h3>
                            <p className="text-sm text-muted-foreground mb-2">
                              {step.description}
                            </p>
                            {step.code && (
                              <div className="relative">
                                <pre className="bg-muted rounded-lg p-3 text-sm font-mono overflow-x-auto">
                                  {step.code}
                                </pre>
                                <button
                                  className="absolute top-2 right-2 p-1.5 rounded-md hover:bg-background/50 transition-colors"
                                  onClick={() =>
                                    copyToClipboard(step.code!, step.step)
                                  }
                                  aria-label="Copy to clipboard"
                                >
                                  {copiedStep === step.step ? (
                                    <CheckCircle className="w-4 h-4 text-green-500" />
                                  ) : (
                                    <Copy className="w-4 h-4 text-muted-foreground" />
                                  )}
                                </button>
                              </div>
                            )}
                          </div>
                        </div>
                        {index < currentSteps.length - 1 && (
                          <Separator className="my-4 ml-12" />
                        )}
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Help callout */}
              <div className="mt-8 bg-blue-500/10 border border-blue-500/20 rounded-lg p-4 flex items-start gap-3">
                <AlertCircle className="w-5 h-5 text-blue-500 shrink-0 mt-0.5" />
                <div>
                  <h4 className="font-medium mb-1">Need help installing?</h4>
                  <p className="text-sm text-muted-foreground">
                    Check out our{" "}
                    <Link href="/docs/installation" className="text-primary hover:underline">
                      detailed installation guide
                    </Link>{" "}
                    or{" "}
                    <a
                      href="mailto:support@splice.app"
                      className="text-primary hover:underline"
                    >
                      contact support
                    </a>
                    .
                  </p>
                </div>
              </div>
            </motion.div>
          </div>
        </section>

        {/* CTA */}
        <section className="py-16">
          <div className="container mx-auto px-4 text-center">
            <h2 className="text-2xl font-bold mb-4">
              Don&apos;t have a license yet?
            </h2>
            <p className="text-muted-foreground mb-6">
              Start your 14-day free trial with full access to all features.
            </p>
            <Button
              size="lg"
              className="bg-gradient-to-r from-violet-600 to-cyan-600 hover:from-violet-700 hover:to-cyan-700"
              asChild
            >
              <Link href="/pricing">
                View Pricing
                <ArrowRight className="w-4 h-4 ml-2" />
              </Link>
            </Button>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

