                  ].map((item) => (
                    <Card key={item.step} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4">
                        <div className="flex gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {item.step}
                          </div>
                          <div className="flex-1">
                            <h4 className="font-semibold text-white mb-1">{item.title}</h4>
                            <p className="text-sm text-gray-400">{item.description}</p>
                            {item.action && (
                              <Button size="sm" className="mt-3 bg-[#00D4FF]/10 text-[#00D4FF] hover:bg-[#00D4FF]/20" asChild>
                                <Link href={item.action.href}>
                                  <Download className="w-4 h-4 mr-2" />
                                  {item.action.label}
                                </Link>
                              </Button>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>

                {/* Windows Steps */}
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Monitor className="w-5 h-5" />
                  Windows Installation
                </h3>
                <div className="space-y-4">
                  {[
                    {
                      step: 1,
                      title: "Download SPLICE UXP",
                      description: "Download the latest UXP version from the downloads page.",
                    },
                    {
                      step: 2,
                      title: "Extract to a folder",
                      description: "Extract the ZIP file to a folder like C:\\Program Files\\SPLICE or your Documents folder.",
                    },
                    {
                      step: 3,
                      title: "Open UXP Developer Tool",
                      description: "In Premiere Pro, navigate to Window > Extensions > UXP Developer Tool.",
                    },
                    {
                      step: 4,
                      title: "Add the Plugin",
                      description: 'Click "Add" and browse to the extracted folder containing manifest.json.',
                    },
                    {
                      step: 5,
                      title: "Activate License",
                      description: "Open SPLICE from Window > Extensions > SPLICE and enter your license key.",
                    },
                  ].map((item) => (
                    <Card key={item.step} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4">
                        <div className="flex gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {item.step}
                          </div>
                          <div>
                            <h4 className="font-semibold text-white mb-1">{item.title}</h4>
                            <p className="text-sm text-gray-400">{item.description}</p>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </TabsContent>

              {/* CEP Installation */}
              <TabsContent value="cep" className="mt-8">
                {/* Requirements */}
                <Card className="bg-[#0d1420] border-gray-800 mb-8">
                  <CardContent className="p-6">
                    <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                      <Monitor className="w-5 h-5 text-[#00D4FF]" />
                      {requirements.cep.title}
                    </h3>
                    <ul className="space-y-2">
                      {requirements.cep.items.map((item, i) => (
                        <li key={i} className="flex items-center gap-2 text-gray-300">
                          <Check className="w-4 h-4 text-[#00D4FF]" />
                          {item}
                        </li>
                      ))}
                    </ul>
                  </CardContent>
                </Card>

                {/* Warning */}
                <Card className="bg-yellow-500/10 border-yellow-500/30 mb-8">
                  <CardContent className="p-4 flex gap-3">
                    <AlertTriangle className="w-5 h-5 text-yellow-500 shrink-0 mt-0.5" />
                    <div>
                      <h4 className="font-semibold text-yellow-500 mb-1">Important: Enable Debug Mode</h4>
                      <p className="text-sm text-yellow-500/80">
                        CEP extensions require debug mode to be enabled. This is safe and required for all third-party CEP extensions.
                      </p>
                    </div>
                  </CardContent>
                </Card>

                {/* macOS CEP Steps */}
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Apple className="w-5 h-5" />
                  macOS Installation
                </h3>
                <div className="space-y-4 mb-8">
                  {[
                    {
                      step: 1,
                      title: "Download SPLICE CEP",
                      description: "Download the CEP version from the downloads page.",
                    },
                    {
                      step: 2,
                      title: "Copy to Extensions folder",
                      description: "Copy the extracted folder to the Adobe extensions directory:",
                      code: "cp -r splice-cep ~/Library/Application\\ Support/Adobe/CEP/extensions/",
                    },
                    {
                      step: 3,
                      title: "Enable Debug Mode",
                      description: "Open Terminal and run this command to enable unsigned extensions:",
                      code: "defaults write com.adobe.CSXS.11 PlayerDebugMode 1",
                    },
                    {
                      step: 4,
                      title: "Restart Premiere Pro",
                      description: "Fully quit Premiere Pro (Cmd+Q) and reopen it. SPLICE will appear in Window > Extensions.",
                    },
                  ].map((item) => (
                    <Card key={item.step} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4">
                        <div className="flex gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {item.step}
                          </div>
                          <div className="flex-1">
                            <h4 className="font-semibold text-white mb-1">{item.title}</h4>
                            <p className="text-sm text-gray-400 mb-2">{item.description}</p>
                            {item.code && (
                              <div className="relative">
                                <pre className="bg-[#0a0f1a] border border-gray-700 rounded-lg p-3 text-sm font-mono overflow-x-auto text-[#00D4FF]">
                                  {item.code}
                                </pre>
                                <button
                                  className="absolute top-2 right-2 p-1.5 rounded-md hover:bg-white/10 transition-colors"
                                  onClick={() => copyToClipboard(item.code!, `cep-mac-${item.step}`)}
                                  aria-label="Copy to clipboard"
                                >
                                  {copiedCommand === `cep-mac-${item.step}` ? (
                                    <CheckCircle className="w-4 h-4 text-[#00D4FF]" />
                                  ) : (
                                    <Copy className="w-4 h-4 text-gray-500" />
                                  )}
                                </button>
                              </div>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>

                {/* Windows CEP Steps */}
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Monitor className="w-5 h-5" />
                  Windows Installation
                </h3>
                <div className="space-y-4">
                  {[
                    {
                      step: 1,
                      title: "Download SPLICE CEP",
                      description: "Download the CEP version from the downloads page.",
                    },
                    {
                      step: 2,
                      title: "Copy to Extensions folder",
                      description: "Copy the extracted folder to:",
                      code: "C:\\Program Files (x86)\\Common Files\\Adobe\\CEP\\extensions\\",
                    },
                    {
                      step: 3,
                      title: "Enable Debug Mode via Registry",
                      description: "Open Registry Editor (regedit), navigate to HKEY_CURRENT_USER\\Software\\Adobe\\CSXS.11, create a new String Value named PlayerDebugMode and set it to 1.",
                    },
                    {
                      step: 4,
                      title: "Restart Premiere Pro",
                      description: "Close and reopen Premiere Pro. Find SPLICE in Window > Extensions.",
                    },
                  ].map((item) => (
                    <Card key={item.step} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4">
                        <div className="flex gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {item.step}
                          </div>
                          <div className="flex-1">
                            <h4 className="font-semibold text-white mb-1">{item.title}</h4>
                            <p className="text-sm text-gray-400 mb-2">{item.description}</p>
                            {item.code && (
                              <div className="relative">
                                <pre className="bg-[#0a0f1a] border border-gray-700 rounded-lg p-3 text-sm font-mono overflow-x-auto text-gray-300">
                                  {item.code}
                                </pre>
                                <button
                                  className="absolute top-2 right-2 p-1.5 rounded-md hover:bg-white/10 transition-colors"
                                  onClick={() => copyToClipboard(item.code!, `cep-win-${item.step}`)}
                                  aria-label="Copy to clipboard"
                                >
                                  {copiedCommand === `cep-win-${item.step}` ? (
                                    <CheckCircle className="w-4 h-4 text-[#00D4FF]" />
                                  ) : (
                                    <Copy className="w-4 h-4 text-gray-500" />
                                  )}
                                </button>
                              </div>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </TabsContent>
            </Tabs>
          </motion.div>

          {/* Troubleshooting */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <Card className="bg-[#0d1420] border-gray-800 mb-8">
              <CardContent className="p-6">
                <h3 className="text-lg font-semibold text-white mb-4">Common Issues</h3>
                <div className="space-y-4">
                  <div>
                    <h4 className="font-medium text-white mb-1">SPLICE doesn&apos;t appear in Extensions menu</h4>
                    <p className="text-sm text-gray-400">Make sure you&apos;ve restarted Premiere Pro after installation. For CEP, verify debug mode is enabled.</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-white mb-1">License activation fails</h4>
                    <p className="text-sm text-gray-400">Check your internet connection and verify the license key is entered correctly without extra spaces.</p>
                  </div>
                  <div>
                    <h4 className="font-medium text-white mb-1">Panel shows blank/white screen</h4>
                    <p className="text-sm text-gray-400">Try closing and reopening the panel, or restart Premiere Pro. This can happen if the panel loads before initialization completes.</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>

          {/* Navigation */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
            className="flex justify-between items-center pt-8 border-t border-gray-800"
          >
            <Link href="/docs/quickstart" className="text-gray-400 hover:text-[#00D4FF] flex items-center">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Quick Start
            </Link>
            <Link href="/docs/features" className="text-[#00D4FF] hover:underline flex items-center">
              Explore Features
              <ArrowRight className="w-4 h-4 ml-2" />
            </Link>
          </motion.div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

