              {/* Close button */}
              <button
                onClick={() => setShowDemo(false)}
                className="absolute top-4 right-4 z-10 p-2 rounded-full bg-black/50 text-white hover:bg-black/70 transition-colors"
              >
                <X className="w-5 h-5" />
              </button>

              {/* Video container with placeholder */}
              <div className="aspect-video bg-gradient-to-br from-[#0d1829] to-[#1a2744] flex flex-col items-center justify-center p-8">
                {/* TODO: Replace with actual video embed once recorded */}
                {/* <video src="/videos/demo.mp4" controls autoPlay className="w-full h-full" /> */}

                {/* Placeholder while video is being created */}
                <div className="text-center">
                  <div className="w-20 h-20 mx-auto mb-6 rounded-full bg-[#00D4FF]/20 flex items-center justify-center">
                    <Play className="w-10 h-10 text-[#00D4FF]" />
