// Demo video configuration - set the URL to enable video playback
// Supports: YouTube (youtube.com/watch?v=...), Vimeo (vimeo.com/...), or self-hosted (.mp4, .webm)
const DEMO_VIDEO_URL = process.env.NEXT_PUBLIC_DEMO_VIDEO_URL || null;

// Helper to parse and render video embed
function getVideoEmbed(url: string): { type: 'youtube' | 'vimeo' | 'native'; embedUrl: string } | null {
  if (!url) return null;

  // YouTube
  const ytMatch = url.match(/(?:youtube\.com\/(?:watch\?v=|embed\/)|youtu\.be\/)([a-zA-Z0-9_-]{11})/);
  if (ytMatch) {
    return { type: 'youtube', embedUrl: `https://www.youtube.com/embed/${ytMatch[1]}?autoplay=1&rel=0` };
  }

  // Vimeo
  const vimeoMatch = url.match(/vimeo\.com\/(?:video\/)?(\d+)/);
  if (vimeoMatch) {
    return { type: 'vimeo', embedUrl: `https://player.vimeo.com/video/${vimeoMatch[1]}?autoplay=1` };
  }

  // Self-hosted video
  if (url.match(/\.(mp4|webm|ogg)$/i) || url.startsWith('/videos/')) {
    return { type: 'native', embedUrl: url };
  }

  return null;
}

