                      <div className="h-full w-[40%] bg-[#00D4FF]" />
                    </div>
                  </div>

                  <button className="w-full mt-2 bg-[#00D4FF]/10 hover:bg-[#00D4FF]/20 text-[#00D4FF] text-xs font-medium py-1.5 rounded border border-[#00D4FF]/30 transition-colors">
