  1177→/**
  1178→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1179→ * @param {string} key - License key to validate
  1180→ * @returns {boolean} True if format is valid
  1181→ */
  1182→function isValidLicenseKeyFormat(key) {
  1183→  if (!key || typeof key !== 'string') return false;
  1184→  const normalized = key.toUpperCase().trim();
  1185→  const pattern = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;
  1186→  return pattern.test(normalized);
  1187→}
