  1110→function formatBuildError(error) {
  1111→  if (!error) return 'Unknown error occurred';
  1112→
  1113→  // Map technical errors to user-friendly messages
  1114→  const errorMap = {
  1115→    'Builder not initialized': 'Builder module failed to load. Try restarting the plugin.',
  1116→    'No active project': 'No project is open. Please open or create a project first.',
  1117→    'No media items found': 'No media files in project. Import media before building.',
  1118→    'No detection data': 'Run analysis first (click GO button).',
  1119→    'Failed to generate cut list': 'Server error generating cuts. Check your connection.',
  1120→    'Failed to build sequence': 'Premiere Pro sequence build failed. Try again.',
  1121→    'fetch failed': 'Network error. Check if backend server is running.',
  1122→    'NetworkError': 'Cannot connect to server. Check your internet connection.',
  1123→    'Insufficient credits': 'Out of credits. Upgrade your plan to continue.',
  1124→    'Authentication required': 'Login required. Click the credits badge to log in.'
  1125→  };
  1126→
  1127→  // Check for matching error patterns
  1128→  for (const [pattern, friendly] of Object.entries(errorMap)) {
  1129→    if (error.includes(pattern)) {
  1130→      return friendly;
  1131→    }
  1132→  }
  1133→
  1134→  // Return original if no match (already reasonably clear)
  1135→  return error;
  1136→}
