  1221→
  1222→// 1. ROBUST CONSTANTS (Fixes 'undefined' error)
  1223→// We define these integers explicitly because UXP API versions vary on where they store Enums.
  1224→const ProjectItemType = {
  1225→    CLIP: 1,
  1226→    BIN: 2,
  1227→    ROOT: 3,
  1228→    FILE: 4
  1229→};
  1230→
  1231→/**
  1232→ * Find the first media item in the project bin (recursively)
  1233→ * @param {Object} item - ProjectItem (usually rootItem)
  1234→ * @returns {Promise<Object|null>} First media item found or null
  1235→ */
  1236→async function findFirstMediaItem(item) {
  1237→  if (!item) return null;
  1238→
  1239→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  1240→  if (item.type === ProjectItemType.CLIP) {
  1241→    return item;
  1242→  }
  1243→
  1244→  // Search Children (Bin or Root)
  1245→  // Note: item.children is a collection, check numItems
  1246→  if (item.children && item.children.numItems > 0) {
  1247→    for (let i = 0; i < item.children.numItems; i++) {
  1248→      const child = item.children[i];
  1249→      const found = await findFirstMediaItem(child);
  1250→      if (found) return found;
  1251→    }
  1252→  }
  1253→
  1254→  return null;
  1255→}
