  1257→/**
  1258→ * Build a sequence from detection data locally
  1259→ * Used for testing via console: await buildSequenceV35(silences)
  1260→ */
  1261→async function buildSequenceV35(silences, takes = []) {
  1262→    if (!window.spliceBuilder) {
  1263→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
  1264→    }
  1265→
  1266→    const app = require('premierepro');
  1267→    const project = await app.Project.getActiveProject();
  1268→    if (!project) throw new Error("No active project found.");
  1269→
  1270→    const rootItem = await project.getRootItem();
  1271→    const sourceProjectItem = await findFirstMediaItem(rootItem);
  1272→
  1273→    if (!sourceProjectItem) {
  1274→        throw new Error('No media items found in project bin.');
