  1282→/**
  1283→ * Request a cut list from the backend and build the sequence.
  1284→ * Full v3.5 workflow.
  1285→ */
  1286→async function buildSequenceWithCutList() {
  1287→  if (!window.spliceBuilder) {
  1288→    throw new Error('Builder not initialized');
  1289→  }
  1290→
  1291→  // Ensure BACKEND_URL is available
  1292→  const apiUrl = (typeof BACKEND_URL !== 'undefined') ? BACKEND_URL : 'http://localhost:3000';
  1293→
  1294→  if (typeof showProgress === 'function') showProgress('Getting source clip info...');
  1295→
  1296→  const app = require('premierepro');
  1297→  const project = await app.Project.getActiveProject();
  1298→  if (!project) throw new Error("No active project.");
  1299→
  1300→  const rootItem = await project.getRootItem();
  1301→  const sourceProjectItem = await findFirstMediaItem(rootItem);
  1302→
  1303→  if (!sourceProjectItem) {
  1304→    throw new Error('No media items found in project. Please import a media file first.');
  1305→  }
  1306→
  1307→  // Use properties strictly
  1308→  const sourceName = sourceProjectItem.name;
  1309→  const sourcePath = sourceProjectItem.treePath;
  1310→
  1311→  const durationObj = await sourceProjectItem.getDuration();
  1312→  const duration = durationObj ? durationObj.seconds : 0;
  1313→
  1314→  // Retrieve Globals
  1315→  const silences = window.splicePreviewState?.getSelectedSilences() || (typeof safeSilences !== 'undefined' ? safeSilences : []);
  1316→  const takes = (typeof previewTakes !== 'undefined' ? previewTakes : []);
  1317→
  1318→  if (silences.length === 0 && (!takes || takes.length === 0)) {
  1319→     throw new Error('No detection data found. Run analysis first.');
  1320→  }
  1321→
  1322→  if (typeof showProgress === 'function') showProgress('Generating cut list...');
  1323→
  1324→  const response = await fetch(`${apiUrl}/cut-list`, {
  1325→    method: 'POST',
  1326→    headers: getAuthHeaders(),
  1327→    body: JSON.stringify({
  1328→      sourceName,
  1329→      sourcePath,
  1330→      duration,
  1331→      silences,
  1332→      takes
  1333→    })
  1334→  });
  1335→
  1336→  if (!response.ok) {
  1337→    const err = await response.json();
  1338→    throw new Error(err.error || 'Failed to generate cut list');
  1339→  }
  1340→
  1341→  const data = await response.json();
  1342→  const cutList = data.cutList;
  1343→
  1344→  if (typeof showProgress === 'function') showProgress('Building sequence...');
  1345→
  1346→  const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, sourceProjectItem);
  1347→
  1348→  if (!result.success) {
  1349→    throw new Error(result.error || 'Failed to build sequence');
  1350→  }
  1351→
  1352→  return {
  1353→    success: true,
  1354→    clipsAdded: result.clipsAdded,
  1355→    metadata: cutList.metadata
  1356→  };
  1357→}
  1358→
  1359→// Expose functions globally for UI and testing
  1360→if (typeof window !== 'undefined') {
  1361→    window.buildSequenceV35 = buildSequenceV35;
  1362→    window.buildSequenceWithCutList = buildSequenceWithCutList;
  1363→    window.findFirstMediaItem = findFirstMediaItem;
  1364→}
