  1369→async function seekToTime(seconds) {
  1370→  // Validate input
  1371→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1372→    setStatus('Invalid seek time');
  1373→    return;
  1374→  }
  1375→
  1376→  if (seconds < 0) {
  1377→    seconds = 0;
  1378→  }
  1379→
  1380→  try {
  1381→    const context = await getActiveSequence();
  1382→    if (!context) {
  1383→      setStatus('No sequence available');
  1384→      return;
  1385→    }
  1386→
  1387→    const { sequence } = context;
  1388→
  1389→    // Use TickTime API to convert seconds to the format needed
  1390→    const position = ppro.TickTime.createWithSeconds(seconds);
  1391→
  1392→    await sequence.setPlayerPosition(position);
  1393→    setStatus(`Seeked to ${formatTime(seconds)}`);
  1394→  } catch (err) {
  1395→    console.error('[SPLICE] Seek error:', err);
  1396→    setStatus('Seek failed: ' + (err.message || 'Unknown error'));
  1397→  }
  1398→}
