  1625→/**
  1626→ * Run batch processing
  1627→ */
  1628→async function runBatchProcessing() {
  1629→  const batchProgress = document.getElementById('batchProgress');
  1630→  const batchProgressBar = document.getElementById('batchProgressBar');
  1631→  const batchProgressText = document.getElementById('batchProgressText');
  1632→
  1633→  if (batchProgress) batchProgress.style.display = 'block';
  1634→
  1635→  try {
  1636→    // Get settings
  1637→    const sensitivity = parseInt(document.getElementById('sensitivitySlider')?.value || 50);
  1638→    const params = mapSensitivity(sensitivity);
  1639→
  1640→    // Call batch endpoint
  1641→    const response = await fetch(`${BACKEND_URL}/batch/silences`, {
  1642→      method: 'POST',
  1643→      headers: getAuthHeaders(),
  1644→      body: JSON.stringify({
  1645→        files: batchQueue,
  1646→        options: {
  1647→          sensitivity,
  1648→          threshold: params.dbThreshold,
  1649→          minSilenceLength: params.minDuration,
  1650→          paddingStart: params.padding,
  1651→          paddingEnd: params.padding / 2
  1652→        }
  1653→      })
  1654→    });
  1655→
  1656→    if (!response.ok) {
  1657→      const err = await response.json();
  1658→      throw new Error(err.error || 'Batch request failed');
  1659→    }
  1660→
  1661→    const data = await response.json();
  1662→    currentBatchJobId = data.jobId;
  1663→    setStatus(`Batch job started: ${data.jobId}`);
  1664→
  1665→    // Poll for status
  1666→    await pollBatchStatus(currentBatchJobId, batchProgressBar, batchProgressText);
  1667→
  1668→  } catch (err) {
  1669→    setStatus('Batch error: ' + err.message);
  1670→  } finally {
  1671→    if (batchProgress) batchProgress.style.display = 'none';
  1672→  }
  1673→}
  1674→
  1675→/**
  1676→ * Poll batch job status
  1677→ */
  1678→async function pollBatchStatus(jobId, progressBar, progressText) {
  1679→  const pollInterval = 2000; // 2 seconds
  1680→  const maxPolls = 300; // 10 minutes max
  1681→  let polls = 0;
  1682→
  1683→  while (polls < maxPolls) {
  1684→    try {
  1685→      const response = await fetch(`${BACKEND_URL}/batch/status/${jobId}`, {
  1686→        headers: getAuthHeaders()
  1687→      });
  1688→
  1689→      if (!response.ok) {
  1690→        throw new Error('Failed to get batch status');
  1691→      }
  1692→
  1693→      const data = await response.json();
  1694→      const job = data.job;
  1695→
  1696→      // Update progress
  1697→      if (progressBar) progressBar.value = job.progress.percentage;
  1698→      if (progressText) progressText.textContent = `${job.progress.completed + job.progress.failed} / ${job.progress.total} files`;
  1699→
  1700→      // Check if complete
  1701→      if (job.status !== 'processing') {
  1702→        if (job.status === 'completed') {
  1703→          setStatus(`Batch complete: ${job.progress.completed} files processed`);
  1704→        } else if (job.status === 'completed_with_errors') {
  1705→          setStatus(`Batch complete with ${job.progress.failed} error(s)`);
  1706→        } else {
  1707→          setStatus('Batch failed');
  1708→        }
  1709→
  1710→        // Clear queue on completion
  1711→        batchQueue = [];
  1712→        updateBatchQueueDisplay();
  1713→        return;
  1714→      }
  1715→
  1716→      await new Promise(resolve => setTimeout(resolve, pollInterval));
  1717→      polls++;
  1718→    } catch (err) {
  1719→      setStatus('Polling error: ' + err.message);
  1720→      return;
  1721→    }
  1722→  }
  1723→
  1724→  setStatus('Batch timed out');
  1725→}
