    body: JSON.stringify({
      wavPath: audioPath,
      threshold: params.dbThreshold,
      minDuration: params.minDuration,
      padding: params.padding
    })
  });

  if (!response.ok) {
    const err = await response.json();
    throw new Error(err.error || 'Silence detection failed');
  }

