        editPreset(presetId);
      }

      if (deleteBtn) {
        const presetId = deleteBtn.dataset.presetId;
        const presetName = deleteBtn.dataset.presetName;
        deletePresetWithConfirm(presetId, presetName);
      }

      if (duplicateBtn) {
