   284â†’// ============================================================================
   285â†’// SEQUENCE EVENT LISTENERS
   286â†’// ============================================================================
   287â†’/**
   288â†’ * Initialize event listeners for sequence state changes
   289â†’ * This provides real-time updates when sequences are activated/deactivated
   290â†’ */
   291â†’function initSequenceEventListeners() {
   292â†’    if (!jsx.cs) {
   293â†’        console.warn('[SPLICE] Cannot init sequence listeners - CSInterface not available');
   294â†’        return;
   295â†’    }
   296â†’
   297â†’    try {
   298â†’        // Listen for sequence activation events
   299â†’        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceActivated', async (event) => {
   300â†’            debugLog('Sequence activated event received');
   301â†’            try {
   302â†’                const seqInfo = await jsx.call('getActiveSequence');
   303â†’                if (seqInfo && !seqInfo.error) {
   304â†’                    window.spliceState.activeSequence = seqInfo;
   305â†’                    setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   306â†’                    debugLog('Active sequence updated:', seqInfo.name);
   307â†’                }
   308â†’            } catch (e) {
   309â†’                console.warn('[SPLICE] Failed to get sequence info on activation:', e.message);
   310â†’            }
   311â†’        });
   312â†’
   313â†’        // Listen for sequence close/deactivation
   314â†’        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceDeactivated', () => {
   315â†’            debugLog('Sequence deactivated event');
   316â†’            window.spliceState.activeSequence = null;
   317â†’            setStatus('No sequence open', true);
   318â†’        });
   319â†’
   320â†’        // Listen for project item selection changes (includes sequences)
   321â†’        jsx.cs.addEventListener('com.adobe.csxs.events.SelectionChanged', async () => {
   322â†’            // Debounce rapid selection changes
   323â†’            if (window._selectionChangeTimeout) {
   324â†’                clearTimeout(window._selectionChangeTimeout);
   325â†’            }
   326â†’            // FIX: Increased debounce to 500ms to allow Premiere Pro state to settle
   327â†’            window._selectionChangeTimeout = setTimeout(async () => {
   328â†’                try {
   329â†’                    // FIX: Use retries (2 attempts, 300ms delay) to handle timing issues
   330â†’                    // Previous code used (0, 0) which caused race conditions
   331â†’                    const hasSequence = await checkSequenceOpen(2, 300);
   332â†’                    if (hasSequence && !window.spliceState.activeSequence) {
   333â†’                        const seqInfo = await jsx.call('getActiveSequence');
   334â†’                        if (seqInfo && !seqInfo.error) {
   335â†’                            window.spliceState.activeSequence = seqInfo;
   336â†’                            setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   337â†’                            debugLog('Sequence detected via selection change:', seqInfo.name);
   338â†’                        }
   339â†’                    }
   340â†’                } catch (e) {
   341â†’                    // FIX: Log errors instead of silently ignoring
   342â†’                    console.warn('[SPLICE] Selection change handler error:', e.message);
   343â†’                    debugLog('Selection change failed:', e.message);
   344â†’                }
   345â†’            }, 500);
   346â†’        });
   347â†’
   348â†’        debugLog('Sequence event listeners initialized');
   349â†’    } catch (e) {
   350â†’        console.error('[SPLICE] Failed to init sequence event listeners:', e.message);
   351â†’    }
   352â†’}
   353â†’
   354â†’// ============================================================================
   355â†’// JSX CONNECTION CHECK
   356â†’// ============================================================================
   357â†’async function checkJSXConnection() {
   358â†’    try {
   359â†’        const result = await jsx.call('getVersion');
   360â†’        debugLog('JSX connection OK, version:', result);
   361â†’        return true;
   362â†’    } catch (e) {
   363â†’        console.warn('[SPLICE] JSX connection failed:', e.message);
   364â†’        setStatus('Premiere Pro connection failed', true);
   365â†’        return false;
   366â†’    }
   367â†’}
   368â†’
   369â†’/**
   370â†’ * Check if a sequence is currently open in the Timeline
   371â†’ * Enhanced with logging and retry logic
   372â†’ * @param {number} retries - Number of retry attempts (default: 2)
   373â†’ * @param {number} delayMs - Delay between retries in ms (default: 300)
   374â†’ * @returns {Promise<boolean>} true if sequence is open
   375â†’ */
   376â†’async function checkSequenceOpen(retries = 2, delayMs = 300) {
   377â†’    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');
   378â†’    console.log('[SPLICE] checkSequenceOpen: retries=' + retries + ', delayMs=' + delayMs);
   379â†’    console.log('[SPLICE] checkSequenceOpen: window.spliceState.activeSequence =', window.spliceState.activeSequence);
   380â†’    
   381â†’    for (let attempt = 0; attempt <= retries; attempt++) {
   382â†’        try {
   383â†’            console.log('[SPLICE] checkSequenceOpen: Attempt ' + (attempt + 1) + ' of ' + (retries + 1));
   384â†’            console.log('[SPLICE] checkSequenceOpen: Calling jsx.call("checkSequenceOpen")...');
   385â†’            const result = await jsx.call('checkSequenceOpen');
   386â†’            console.log('[SPLICE] checkSequenceOpen: JSX call returned');
   387â†’            console.log('[SPLICE] checkSequenceOpen: result =', result, '(type:', typeof result + ')');
   388â†’            debugLog('checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   389â†’
   390â†’            if (result === true || result === 'true') {
   391â†’                console.log('[SPLICE] checkSequenceOpen: Returning TRUE - sequence is open');
   392â†’                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   393â†’                return true;
   394â†’            }
   395â†’
   396â†’            // If false, wait and retry (sequence might be loading)
   397â†’            if (attempt < retries) {
   398â†’                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   399â†’                debugLog('checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   400â†’                await new Promise(r => setTimeout(r, delayMs));
   401â†’            }
   402â†’        } catch (error) {
   403â†’            console.error('[SPLICE] checkSequenceOpen: Attempt', attempt + 1, 'failed with exception:', error.message);
   404â†’            console.error('[SPLICE] checkSequenceOpen: Error stack:', error.stack);
   405â†’
   406â†’            // On last attempt, return false but log the actual error
   407â†’            if (attempt === retries) {
   408â†’                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   409â†’                // Store error for diagnostics
   410â†’                window.spliceState.lastSequenceCheckError = error.message;
   411â†’                console.log('[SPLICE] checkSequenceOpen: Stored error in window.spliceState.lastSequenceCheckError:', error.message);
   412â†’                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   413â†’                return false;
   414â†’            }
   415â†’
   416â†’            // Wait before retry
   417â†’            console.log('[SPLICE] checkSequenceOpen: Waiting', delayMs, 'ms before retry...');
   418â†’            await new Promise(r => setTimeout(r, delayMs));
   419â†’        }
   420â†’    }
   421â†’    console.log('[SPLICE] checkSequenceOpen: All attempts completed without finding sequence');
   422â†’    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   423â†’    return false;
   424â†’}
