   426â†’// ============================================================================
   427â†’// OPTIONS TOGGLE
   428â†’// ============================================================================
   429â†’function initOptionsToggle() {
   430â†’    debugLog('initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   431â†’    if (ui.optionsToggle && ui.optionsPanel) {
   432â†’        ui.optionsToggle.addEventListener('click', () => {
   433â†’            debugLog('Options toggle clicked');
   434â†’            // Check current state BEFORE toggling
   435â†’            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   436â†’            ui.optionsPanel.classList.toggle('collapsed');
   437â†’            // If it was collapsed, it's now expanded (show 'expanded' class)
   438â†’            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   439â†’            saveSettings({ expandedOptions: wasCollapsed });
   440â†’        });
   441â†’
   442â†’        // Restore state
   443â†’        const settings = getSettings();
   444â†’        if (!settings.expandedOptions) {
   445â†’            ui.optionsPanel.classList.add('collapsed');
   446â†’            ui.optionsToggle.classList.remove('expanded');
   447â†’        } else {
   448â†’            ui.optionsToggle.classList.add('expanded');
   449â†’            ui.optionsPanel.classList.remove('collapsed');
   450â†’        }
   451â†’    }
   452â†’}
