   457â†’function initTextEditorToggle() {
   458â†’    const toggle = document.getElementById('textEditorToggle');
   459â†’    const panel = document.getElementById('text-editor-panel');
   460â†’    debugLog('initTextEditorToggle - toggle:', !!toggle, 'panel:', !!panel);
   461â†’
   462â†’    if (toggle && panel) {
   463â†’        toggle.addEventListener('click', (e) => {
   464â†’            e.preventDefault();
   465â†’            e.stopPropagation();
   466â†’            debugLog('Text Editor toggle clicked');
   467â†’            setStatus('Text Editor: ' + (panel.classList.contains('collapsed') ? 'Opening' : 'Closing'));
   468â†’            // Check state BEFORE toggling
   469â†’            const wasCollapsed = panel.classList.contains('collapsed');
   470â†’            panel.classList.toggle('collapsed');
   471â†’            const icon = toggle.querySelector('.toggle-icon');
   472â†’            debugLog('wasCollapsed:', wasCollapsed, 'icon:', !!icon);
   473â†’            if (icon) {
   474â†’                // If it was collapsed, it's now expanded (show -)
   475â†’                icon.textContent = wasCollapsed ? '-' : '+';
   476â†’            }
   477â†’        });
   478â†’    } else {
   479â†’        console.error('[SPLICE] Text Editor toggle elements not found!');
   480â†’    }
   481â†’}
