   635â†’function applyPreset(presetId) {
   636â†’    const preset = PRESETS[presetId];
   637â†’    if (!preset) return;
   638â†’
   639â†’    // Custom preset - keep current settings
   640â†’    if (presetId === 'custom' || !preset.settings) {
   641â†’        saveSettings({ activePreset: 'custom' });
   642â†’        setStatus('Using custom settings');
   643â†’        return;
   644â†’    }
   645â†’
   646â†’    const settings = preset.settings;
   647â†’
   648â†’    // Apply to UI
   649â†’    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   650â†’        ui.sensitivitySlider.value = settings.sensitivity;
   651â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   652â†’    }
   653â†’
   654â†’    if (ui.enableJCut) {
   655â†’        ui.enableJCut.checked = settings.enableJCut || false;
   656â†’        if (ui.jcutSettings) {
   657â†’            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   658â†’        }
   659â†’    }
   660â†’
   661â†’    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   662â†’        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   663â†’        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   664â†’    }
   665â†’
   666â†’    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   667â†’        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   668â†’        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   669â†’    }
   670â†’
   671â†’    // Save merged settings
   672â†’    saveSettings({
   673â†’        activePreset: presetId,
   674â†’        sensitivity: settings.sensitivity,
   675â†’        threshold: settings.threshold,
   676â†’        minSilenceLength: settings.minSilenceLength,
   677â†’        paddingStart: settings.paddingStart,
   678â†’        paddingEnd: settings.paddingEnd,
   679â†’        enableTakesDetection: settings.enableTakesDetection,
   680â†’        enableJCut: settings.enableJCut || false,
   681â†’        jcutLeadIn: settings.jCutLeadIn || 0.3,
   682â†’        jcutLeadOut: settings.jCutLeadOut || 0.2
   683â†’    });
   684â†’
   685â†’    setStatus(`Applied ${preset.name} preset`);
   686â†’}
