   744â†’    if (!hasSequence) {
   745â†’        // Check if there was a communication error vs just no sequence
   746â†’        const lastError = window.spliceState.lastSequenceCheckError;
   747â†’        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   748â†’            setStatus('Premiere Pro connection issue - try reopening panel', true);
   749â†’            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
