   782â†’
   783â†’        // Export audio via JSX for silence detection
   784â†’        updateProgress(10, 'Exporting audio...');
   785â†’
   786â†’        let audioFilePath = null;
   787â†’        try {
   788â†’            // FIX: CEP-CRIT-010 - Main.js Export Timeout
