  1135â†’async function seekToTime(seconds) {
  1136â†’    try {
  1137â†’        // FIX: Removed invalid 'app' check - 'app' is ExtendScript DOM, not available in panel JS
  1138â†’        // The JSX evalScript will handle the error case if no sequence is open
  1139â†’
  1140â†’        // Use JSX to set playhead position
  1141â†’        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1142â†’        const ticks = Math.round(seconds * 254016000000);
  1143â†’        const result = await jsx.evalScript(`
  1144â†’            (function() {
  1145â†’                try {
  1146â†’                    if (!app.project.activeSequence) {
  1147â†’                        return JSON.stringify({ error: 'No active sequence' });
  1148â†’                    }
  1149â†’                    app.project.activeSequence.setPlayerPosition('${ticks}');
  1150â†’                    return JSON.stringify({ success: true });
  1151â†’                } catch (e) {
  1152â†’                    return JSON.stringify({ error: e.message });
  1153â†’                }
  1154â†’            })()
  1155â†’        `);
  1156â†’
  1157â†’        // Check for errors
  1158â†’        if (result && typeof result === 'string') {
  1159â†’            try {
  1160â†’                const parsed = JSON.parse(result);
  1161â†’                if (parsed.error) {
  1162â†’                    debugLog('Seek failed:', parsed.error);
  1163â†’                }
  1164â†’            } catch (parseErr) {
  1165â†’                // Result wasn't JSON, ignore
  1166â†’            }
  1167â†’        }
  1168â†’    } catch (e) {
  1169â†’        console.warn('[SPLICE] Seek failed:', e);
  1170â†’    }
  1171â†’}
